import axios from 'axios';

const API_BASE_URL = 'http://localhost:5033/api';

// Create axios instance with default config
const api = axios.create({
  baseURL: API_BASE_URL,
  headers: {
    'Content-Type': 'application/json',
  },
});
// Add token to requests if it exists
api.interceptors.request.use((config) => {
  const token = localStorage.getItem('token');
  if (token) {
    config.headers.Authorization = `Bearer ${token}`;
  }
  return config;
});

/*api.interceptors.request.use(
  (config) => {
    const token = localStorage.getItem('token');
    if (token) {
      config.headers.Authorization = `Bearer ${token}`;
    }
    return config;
  },
  (error) => Promise.reject(error)
);*/



// Auth API
export const authAPI = {
  login: (email, password) =>
    api.post('/auth/login', { email, password }), // ✅ FIXED
  register: (fullName, email, mobile, password) =>
    api.post('/auth/register', { fullName, email, mobile, password }),
  getProfile: () => api.get('/user/profile'),
};
/*export const authAPI = {
  login: (email, password) => 
    api.post('/auth/login', { username: email, password }),
  register: (fullName, email, mobile, password) =>
    api.post('/auth/register', { fullName, email, mobile, password }),
  getProfile: () => 
    api.get('/user/profile'),
};*/

// Products API
export const productsAPI = {
  getAll: () => 
    api.get('/products'),

  search: (query, categoryId, minPrice, maxPrice) =>
    api.get('/products/search', { params: { query, categoryId, minPrice, maxPrice } }),

  getById: (id) => 
    api.get(`/products/${id}`),
};

// Cart API
export const cartAPI = {
  view: () => 
    api.get('/cart/view'),

  add: (productId, quantity = 1) =>
    api.post('/cart/add', { productId, quantity }),

  remove: (productId) =>
    api.delete(`/cart/remove/${productId}`),

  update: (productId, quantity) =>
    api.put('/cart/update', { productId, quantity }),
};

// Orders API
export const ordersAPI = {
  confirm: (shippingAddressId) =>
    api.post('/order/confirm', { shippingAddressId }),

  history: () =>
    api.get('/order/history'),

  getById: (id) =>
    api.get(`/order/${id}`),
};

// Recommendations API (for AI agent)
export const recommendationsAPI = {
  rank: (productIds, criteria) =>
    api.post('/recommendations/rank', { productIds, criteria }),
};

export default api;
