    if (existing) {
      existing.quantity += 1;
    } else {
      updatedCart.push({
        productId: product.id,
        productName: product.name,
        price: product.price,
        quantity: 1,
        imageUrl: product.image
      });
    }

    setCart(updatedCart);
    localStorage.setItem('cart', JSON.stringify(updatedCart));
    setError('Login to save your cart permanently.');
  }
};

