          for (const addr of profileData.addresses) {
            await addressAPI.remove(addr.id);
          }
          await fetchAddresses();
          alert('All addresses deleted');
        } catch (err) {
          console.error('Delete error:', err);
          alert('Failed to delete some addresses');
        }
      }}
