import React, { useState, useEffect, useRef } from 'react';
import { ShoppingCart, MessageCircle, Search, X, Send, Package, TrendingUp, Shield, Bot } from 'lucide-react';

// Complete E-Commerce Frontend with AI Shopping Agent

const ECommercePlatform = () => {
  const [currentView, setCurrentView] = useState('home');
  const [products, setProducts] = useState([]);
  const [cart, setCart] = useState([]);
  const [chatOpen, setChatOpen] = useState(false);
  const [chatMessages, setChatMessages] = useState([]);
  const [currentMessage, setCurrentMessage] = useState('');
  const [isTyping, setIsTyping] = useState(false);
  const chatEndRef = useRef(null);

  // Sample products (in real app, fetch from API)
  useEffect(() => {
    const sampleProducts = [
      {
        id: 1,
        name: 'Premium Smartphone X1',
        brand: 'TechBrand',
        price: 9999,
        rating: 4.5,
        reviewCount: 1250,
        image: 'https://via.placeholder.com/300x300/3b82f6/ffffff?text=Smartphone',
        category: 'Electronics',
        description: 'Powerful processor, stunning camera, all-day battery',
        specs: '6GB RAM, 128GB Storage, 48MP Camera'
      },
      {
        id: 2,
        name: 'Wireless Earbuds Pro',
        brand: 'AudioMax',
        price: 2999,
        rating: 4.7,
        reviewCount: 890,
        image: 'https://via.placeholder.com/300x300/8b5cf6/ffffff?text=Earbuds',
        category: 'Audio',
        description: 'Active noise cancellation, 30hr battery life',
        specs: 'Bluetooth 5.2, IPX4 Water Resistant'
      },
      {
        id: 3,
        name: 'Smart Watch Ultra',
        brand: 'FitTech',
        price: 4999,
        rating: 4.3,
        reviewCount: 567,
        image: 'https://via.placeholder.com/300x300/10b981/ffffff?text=Watch',
        category: 'Wearables',
        description: 'Health tracking, GPS, always-on display',
        specs: 'Heart Rate Monitor, Sleep Tracking, 7-day Battery'
      },
      {
        id: 4,
        name: 'Gaming Laptop Z9',
        brand: 'GameForce',
        price: 59999,
        rating: 4.8,
        reviewCount: 432,
        image: 'https://via.placeholder.com/300x300/ef4444/ffffff?text=Laptop',
        category: 'Computers',
        description: 'High-performance gaming, RGB keyboard',
        specs: 'RTX 3060, 16GB RAM, 512GB SSD, 144Hz Display'
      },
      {
        id: 5,
        name: 'Camera DSLR Pro',
        brand: 'PhotoMax',
        price: 45999,
        rating: 4.6,
        reviewCount: 289,
        image: 'https://via.placeholder.com/300x300/f59e0b/ffffff?text=Camera',
        category: 'Photography',
        description: 'Professional photography, 4K video',
        specs: '24MP Sensor, Dual Card Slots, Weather Sealed'
      },
      {
        id: 6,
        name: 'Tablet Pro 11',
        brand: 'TechBrand',
        price: 34999,
        rating: 4.4,
        reviewCount: 678,
        image: 'https://via.placeholder.com/300x300/06b6d4/ffffff?text=Tablet',
        category: 'Electronics',
        description: 'Stunning display, all-day battery, perfect for work',
        specs: '11-inch Display, 128GB Storage, Stylus Support'
      }
    ];
    setProducts(sampleProducts);
  }, []);

  // AI Chat simulation
  const sendMessageToAgent = async (message) => {
    setIsTyping(true);

    // Add user message
    const userMsg = { role: 'user', content: message };
    setChatMessages(prev => [...prev, userMsg]);
    setCurrentMessage('');

    // Simulate AI response (in real app, call AI agent API)
    setTimeout(() => {
      let agentResponse = '';
      const lowerMsg = message.toLowerCase();

      if (lowerMsg.includes('hi') || lowerMsg.includes('hello')) {
        agentResponse = "Hello! I'm your AI shopping assistant. I can help you find products, add items to cart, or answer questions about your orders. What are you looking for today?";
      } else if (lowerMsg.includes('smartphone') || lowerMsg.includes('phone')) {
        const phones = products.filter(p => p.category === 'Electronics');
        if (lowerMsg.includes('under') || lowerMsg.includes('below')) {
          agentResponse = "I found some great smartphones under â‚¹10,000! Here's the best option:\n\nðŸ“± Premium Smartphone X1 by TechBrand\nPrice: â‚¹9,999\nRating: 4.5/5 (1,250 reviews)\nFeatures: 6GB RAM, 128GB Storage, 48MP Camera\n\nWould you like me to add this to your cart?";
        } else {
          agentResponse = `I found ${phones.length} smartphones for you! The Premium Smartphone X1 is our top pick with excellent camera and battery life. Would you like more details?`;
        }
      } else if (lowerMsg.includes('add') && lowerMsg.includes('cart')) {
        const newItem = products[0];
        setCart(prev => [...prev, { ...newItem, quantity: 1 }]);
        agentResponse = `Great choice! I've added ${newItem.name} to your cart. Your cart now has ${cart.length + 1} items. Would you like to continue shopping or proceed to checkout?`;
      } else if (lowerMsg.includes('cart') || lowerMsg.includes('show')) {
        if (cart.length === 0) {
          agentResponse = "Your cart is currently empty. Would you like me to help you find some products?";
        } else {
          const total = cart.reduce((sum, item) => sum + item.price * item.quantity, 0);
          agentResponse = `You have ${cart.length} items in your cart:\n\n${cart.map(item => `â€¢ ${item.name} - â‚¹${item.price}`).join('\n')}\n\nTotal: â‚¹${total}\n\nReady to checkout?`;
        }
      } else if (lowerMsg.includes('recommend') || lowerMsg.includes('suggest')) {
        agentResponse = "Based on popular choices, I recommend:\n\n1. Premium Smartphone X1 - Best value for money\n2. Wireless Earbuds Pro - Great audio quality\n3. Smart Watch Ultra - Perfect for fitness tracking\n\nWhich category interests you most?";
      } else if (lowerMsg.includes('checkout') || lowerMsg.includes('buy')) {
        agentResponse = "Perfect! To proceed with checkout, I'll need to confirm your shipping address. You have 1 saved address. Would you like to use it, or add a new one?";
      } else {
        agentResponse = "I can help you with:\nâ€¢ Finding products by category or budget\nâ€¢ Adding items to your cart\nâ€¢ Checking order status\nâ€¢ Product recommendations\n\nWhat would you like to do?";
      }

      const assistantMsg = { role: 'assistant', content: agentResponse };
      setChatMessages(prev => [...prev, assistantMsg]);
      setIsTyping(false);
    }, 1000 + Math.random() * 1000);
  };

  const addToCart = (product) => {
    const existing = cart.find(item => item.id === product.id);
    if (existing) {
      setCart(cart.map(item => 
        item.id === product.id 
          ? { ...item, quantity: item.quantity + 1 }
          : item
      ));
    } else {
      setCart([...cart, { ...product, quantity: 1 }]);
    }
  };

  useEffect(() => {
    chatEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [chatMessages]);

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <header className="bg-white shadow-sm sticky top-0 z-40">
        <div className="max-w-7xl mx-auto px-4 py-4">
          <div className="flex items-center justify-between">
            <div className="flex items-center gap-2">
              <Package className="w-8 h-8 text-blue-600" />
              <h1 className="text-2xl font-bold text-gray-900">ShopAI</h1>
              <span className="text-xs bg-blue-100 text-blue-800 px-2 py-1 rounded-full">AI Powered</span>
            </div>

            <div className="flex-1 max-w-xl mx-8">
              <div className="relative">
                <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 w-5 h-5" />
                <input
                  type="text"
                  placeholder="Search products..."
                  className="w-full pl-10 pr-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                />
              </div>
            </div>

            <div className="flex items-center gap-4">
              <button 
                onClick={() => setCurrentView('cart')}
                className="relative p-2 hover:bg-gray-100 rounded-lg transition-colors"
              >
                <ShoppingCart className="w-6 h-6 text-gray-700" />
                {cart.length > 0 && (
                  <span className="absolute -top-1 -right-1 bg-red-500 text-white text-xs w-5 h-5 rounded-full flex items-center justify-center">
                    {cart.length}
                  </span>
                )}
              </button>
            </div>
          </div>
        </div>
      </header>

      {/* Main Content */}
      <main className="max-w-7xl mx-auto px-4 py-8">
        {currentView === 'home' && (
          <>
            {/* Hero Section */}
            <div className="bg-gradient-to-r from-blue-600 to-purple-600 rounded-2xl p-8 mb-8 text-white">
              <div className="flex items-center justify-between">
                <div>
                  <h2 className="text-3xl font-bold mb-2">Shop Smarter with AI</h2>
                  <p className="text-blue-100 mb-4">Get personalized recommendations from our AI shopping assistant</p>
                  <button 
                    onClick={() => setChatOpen(true)}
                    className="bg-white text-blue-600 px-6 py-3 rounded-lg font-semibold hover:bg-blue-50 transition-colors flex items-center gap-2"
                  >
                    <Bot className="w-5 h-5" />
                    Talk to AI Assistant
                  </button>
                </div>
                <div className="hidden md:flex gap-8">
                  <div className="text-center">
                    <TrendingUp className="w-8 h-8 mx-auto mb-2" />
                    <div className="text-2xl font-bold">24/7</div>
                    <div className="text-sm text-blue-100">AI Support</div>
                  </div>
                  <div className="text-center">
                    <Shield className="w-8 h-8 mx-auto mb-2" />
                    <div className="text-2xl font-bold">100%</div>
                    <div className="text-sm text-blue-100">Secure</div>
                  </div>
                </div>
              </div>
            </div>

            {/* Products Grid */}
            <div>
              <h3 className="text-2xl font-bold text-gray-900 mb-6">Featured Products</h3>
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                {products.map(product => (
                  <div key={product.id} className="bg-white rounded-xl shadow-sm hover:shadow-md transition-shadow overflow-hidden">
                    <img 
                      src={product.image} 
                      alt={product.name}
                      className="w-full h-48 object-cover"
                    />
                    <div className="p-4">
                      <div className="text-sm text-gray-500 mb-1">{product.brand}</div>
                      <h4 className="font-semibold text-gray-900 mb-2">{product.name}</h4>
                      <p className="text-sm text-gray-600 mb-2">{product.description}</p>
                      <div className="flex items-center gap-2 mb-3">
                        <div className="flex items-center">
                          <span className="text-yellow-500">â˜…</span>
                          <span className="text-sm font-medium ml-1">{product.rating}</span>
                        </div>
                        <span className="text-sm text-gray-500">({product.reviewCount})</span>
                      </div>
                      <div className="flex items-center justify-between">
                        <div className="text-2xl font-bold text-gray-900">â‚¹{product.price.toLocaleString()}</div>
                        <button 
                          onClick={() => addToCart(product)}
                          className="bg-blue-600 text-white px-4 py-2 rounded-lg hover:bg-blue-700 transition-colors text-sm font-medium"
                        >
                          Add to Cart
                        </button>
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          </>
        )}

        {currentView === 'cart' && (
          <div className="bg-white rounded-xl shadow-sm p-6">
            <div className="flex items-center justify-between mb-6">
              <h2 className="text-2xl font-bold text-gray-900">Shopping Cart</h2>
              <button 
                onClick={() => setCurrentView('home')}
                className="text-blue-600 hover:text-blue-700"
              >
                Continue Shopping
              </button>
            </div>

            {cart.length === 0 ? (
              <div className="text-center py-12">
                <ShoppingCart className="w-16 h-16 text-gray-300 mx-auto mb-4" />
                <p className="text-gray-500 mb-4">Your cart is empty</p>
                <button 
                  onClick={() => setCurrentView('home')}
                  className="bg-blue-600 text-white px-6 py-2 rounded-lg hover:bg-blue-700"
                >
                  Start Shopping
                </button>
              </div>
            ) : (
              <>
                <div className="space-y-4 mb-6">
                  {cart.map(item => (
                    <div key={item.id} className="flex items-center gap-4 p-4 border border-gray-200 rounded-lg">
                      <img src={item.image} alt={item.name} className="w-20 h-20 object-cover rounded" />
                      <div className="flex-1">
                        <h4 className="font-semibold text-gray-900">{item.name}</h4>
                        <p className="text-sm text-gray-500">{item.brand}</p>
                      </div>
                      <div className="text-right">
                        <div className="font-semibold text-gray-900">â‚¹{item.price.toLocaleString()}</div>
                        <div className="text-sm text-gray-500">Qty: {item.quantity}</div>
                      </div>
                    </div>
                  ))}
                </div>

                <div className="border-t border-gray-200 pt-4">
                  <div className="flex justify-between items-center mb-4">
                    <span className="text-lg font-semibold">Total:</span>
                    <span className="text-2xl font-bold text-gray-900">
                      â‚¹{cart.reduce((sum, item) => sum + item.price * item.quantity, 0).toLocaleString()}
                    </span>
                  </div>
                  <button className="w-full bg-blue-600 text-white py-3 rounded-lg font-semibold hover:bg-blue-700 transition-colors">
                    Proceed to Checkout
                  </button>
                </div>
              </>
            )}
          </div>
        )}
      </main>

      {/* AI Chat Widget */}
      {chatOpen && (
        <div className="fixed bottom-4 right-4 w-96 bg-white rounded-xl shadow-2xl flex flex-col z-50" style={{ height: '600px' }}>
          <div className="bg-gradient-to-r from-blue-600 to-purple-600 text-white p-4 rounded-t-xl flex items-center justify-between">
            <div className="flex items-center gap-2">
              <Bot className="w-6 h-6" />
              <div>
                <div className="font-semibold">AI Shopping Assistant</div>
                <div className="text-xs text-blue-100">Always here to help</div>
              </div>
            </div>
            <button onClick={() => setChatOpen(false)} className="hover:bg-white/20 p-1 rounded">
              <X className="w-5 h-5" />
            </button>
          </div>

          <div className="flex-1 overflow-y-auto p-4 space-y-3">
            {chatMessages.length === 0 && (
              <div className="text-center text-gray-500 mt-8">
                <Bot className="w-12 h-12 mx-auto mb-2 text-gray-300" />
                <p className="text-sm">Hi! I'm your AI assistant.</p>
                <p className="text-xs">Ask me anything about products!</p>
              </div>
            )}
            {chatMessages.map((msg, idx) => (
              <div key={idx} className={`flex ${msg.role === 'user' ? 'justify-end' : 'justify-start'}`}>
                <div className={`max-w-[80%] p-3 rounded-lg ${
                  msg.role === 'user' 
                    ? 'bg-blue-600 text-white' 
                    : 'bg-gray-100 text-gray-900'
                }`}>
                  <p className="text-sm whitespace-pre-line">{msg.content}</p>
                </div>
              </div>
            ))}
            {isTyping && (
              <div className="flex justify-start">
                <div className="bg-gray-100 p-3 rounded-lg">
                  <div className="flex gap-1">
                    <div className="w-2 h-2 bg-gray-400 rounded-full animate-bounce" style={{ animationDelay: '0ms' }}></div>
                    <div className="w-2 h-2 bg-gray-400 rounded-full animate-bounce" style={{ animationDelay: '150ms' }}></div>
                    <div className="w-2 h-2 bg-gray-400 rounded-full animate-bounce" style={{ animationDelay: '300ms' }}></div>
                  </div>
                </div>
              </div>
            )}
            <div ref={chatEndRef} />
          </div>

          <div className="p-4 border-t border-gray-200">
            <div className="flex gap-2">
              <input
                type="text"
                value={currentMessage}
                onChange={(e) => setCurrentMessage(e.target.value)}
                onKeyPress={(e) => e.key === 'Enter' && currentMessage.trim() && sendMessageToAgent(currentMessage)}
                placeholder="Type your message..."
                className="flex-1 px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
              />
              <button
                onClick={() => currentMessage.trim() && sendMessageToAgent(currentMessage)}
                disabled={!currentMessage.trim() || isTyping}
                className="bg-blue-600 text-white p-2 rounded-lg hover:bg-blue-700 disabled:bg-gray-300 disabled:cursor-not-allowed transition-colors"
              >
                <Send className="w-5 h-5" />
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Floating Chat Button */}
      {!chatOpen && (
        <button
          onClick={() => setChatOpen(true)}
          className="fixed bottom-6 right-6 bg-gradient-to-r from-blue-600 to-purple-600 text-white p-4 rounded-full shadow-lg hover:shadow-xl transition-all z-50"
        >
          <MessageCircle className="w-6 h-6" />
        </button>
      )}
    </div>
  );
};

export default ECommercePlatform;
