// âœ… UPDATED: Update cart quantity with proper logging
const updateCartQuantity = async (productId, quantity) => {
  console.group('ðŸ”„ Update Cart Quantity');
  console.log('Product ID:', productId);
  console.log('New Quantity:', quantity);

  if (quantity <= 0) {
    console.log('Quantity is 0 or less, removing item');
    console.groupEnd();
    await removeFromCart(productId);
    return;
  }

  if (user) {
    try {
      const userId = getUserIdFromToken();

      if (!userId) {
        console.error('âŒ No user ID found');
        console.groupEnd();
        setError('User session invalid. Please login again.');
        return;
      }

      // Ensure productId is a string
      const productIdString = String(productId);

      console.log('Making API call:', {
        userId: userId,
        productId: productIdString,
        quantity: quantity
      });

      await cartAPI.update(userId, productIdString, quantity);

      console.log('âœ… API call successful');

      // Update local state
      setCart(prev => {
        const updatedCart = prev.map(item => {
          // Check both productId and productIdString
          const itemId = String(item.productIdString || item.productId);
          const matches = itemId === productIdString;

          console.log('Checking item:', {
            itemId: itemId,
            productIdString: productIdString,
            matches: matches
          });

          return matches ? { ...item, quantity } : item;
        });

        console.log('Updated cart:', updatedCart);
        localStorage.setItem('cart', JSON.stringify(updatedCart));
        return updatedCart;
      });

      setError(''); // Clear any previous errors
      console.log('âœ… Cart updated successfully');
      console.groupEnd();

    } catch (err) {
      console.error('âŒ Error updating cart:', err);
      console.error('Error response:', err.response?.data);
      console.groupEnd();
      setError(err.response?.data?.message || 'Failed to update cart');
    }
  } else {
    // Guest user
    console.log('Guest user - updating localStorage');
    setCart(prev => {
      const updatedCart = prev.map(item =>
        String(item.productId) === String(productId)
          ? { ...item, quantity }
          : item
      );
      localStorage.setItem('cart', JSON.stringify(updatedCart));
      return updatedCart;
    });
    console.log('âœ… Guest cart updated');
    console.groupEnd();
  }
};

