import React, { useState, useEffect, useRef } from 'react';
import { Mail, Lock, User, Phone, Eye, EyeOff, ShoppingBag, TrendingUp, Package, Users, BarChart3, Settings, MessageCircle, Search, X, Send, ShoppingCart, LogOut, UserCircle, MapPin, CreditCard, Clock, Plus, Minus, Trash2 } from 'lucide-react';
import {
  authAPI,
  productsAPI,
  cartAPI,
  ordersAPI,
  recommendationsAPI
} from './api'; // ðŸ‘ˆ adjust path if needed

import axios from 'axios';

