import React, { useState, useEffect, useRef } from 'react';
import { Mail, Lock, User, Phone, Eye, EyeOff, ShoppingBag, TrendingUp, Package, Users, BarChart3, Settings, MessageCircle, Search, X, Send, ShoppingCart, LogOut, UserCircle, MapPin, CreditCard, Clock, Plus, Minus, Trash2 } from 'lucide-react';
import {
  authAPI,
  productsAPI,
  cartAPI,
  ordersAPI,
  recommendationsAPI
} from './api'; // adjust path if needed

import axios from 'axios';

/*// API Configuration
const API_BASE_URL = 'http://localhost:5033/api';

const api = axios.create({
  baseURL: API_BASE_URL,
  headers: {
    'Content-Type': 'application/json',
  },
});

// Request interceptor to add auth token
api.interceptors.request.use(
  (config) => {
    const token = sessionStorage.getItem('token');
    if (token) {
      config.headers.Authorization = `Bearer ${token}`;
    }
    return config;
  },
  (error) => Promise.reject(error)
);

// API endpoints
const authAPI = {
  login: (email, password) => 
    api.post('/auth/login', { email, password }),
  register: (fullName, email, mobile, password) =>
    api.post('/auth/register', { fullName, email, mobile, password }),
  getProfile: () => 
    api.get('/user/profile'),
};

const productsAPI = {
  getAll: () => 
    api.get('/products'),
  search: (query, categoryId, minPrice, maxPrice) =>
    api.get('/products/search', { params: { query, categoryId, minPrice, maxPrice } }),
  getById: (id) => 
    api.get(`/products/${id}`),
};

const cartAPI = {
  view: () => 
    api.get('/cart/view'),
  add: (productId, quantity = 1) =>
    api.post('/cart/add', { productId, quantity }),
  remove: (productId) =>
    api.delete(`/cart/remove/${productId}`),
  update: (productId, quantity) =>
    api.put('/cart/update', { productId, quantity }),
};

const ordersAPI = {
  confirm: (shippingAddressId) =>
    api.post('/order/confirm', { shippingAddressId }),
  history: () =>
    api.get('/order/history'),
  getById: (id) =>
    api.get(`/order/${id}`),
};

const recommendationsAPI = {
  rank: (productIds, criteria) =>
    api.post('/recommendations/rank', { productIds, criteria }),
};*/

const CompleteIntegratedApp = () => {
  const [currentPage, setCurrentPage] = useState('home');
  const [user, setUser] = useState(null);
  const [showPassword, setShowPassword] = useState(false);
  const [loginData, setLoginData] = useState({ email: '', password: '' });
  const [registerData, setRegisterData] = useState({ fullName: '', email: '', mobile: '', password: '', confirmPassword: '' });
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const [activeTab, setActiveTab] = useState('overview');

  // Shopping state
  const [products, setProducts] = useState([]);
  const [cart, setCart] = useState([]);
  const [orders, setOrders] = useState([]);
  const [chatOpen, setChatOpen] = useState(false);
  const [chatMessages, setChatMessages] = useState([]);
  const [currentMessage, setCurrentMessage] = useState('');
  const [isTyping, setIsTyping] = useState(false);
  const chatEndRef = useRef(null);

  // Profile state
  const [editMode, setEditMode] = useState(false);
  const [profileData, setProfileData] = useState({
    fullName: '',
    email: '',
    mobile: '',
    addresses: []
  });

  // Check for existing session on mount
  useEffect(() => {
    const token = sessionStorage.getItem('token');
    const userData = sessionStorage.getItem('user');
    if (token && userData) {
      try {
        setUser(JSON.parse(userData));
        setCurrentPage('products');
        loadUserData();
      } catch (err) {
        console.error('Error parsing user data:', err);
        sessionStorage.removeItem('token');
        sessionStorage.removeItem('user');
      }
    }
  }, []);

  // Load user data (profile, cart, products)
  const loadUserData = async () => {
    try {
      await Promise.all([
        loadProducts(),
        loadCart(),
        loadProfile(),
        loadOrders()
      ]);
    } catch (err) {
      console.error('Error loading user data:', err);
    }
  };

  // Load products from backend
  const loadProducts = async () => {
    try {
      const response = await productsAPI.getAll();
      setProducts(response.data);
    } catch (err) {
      console.error('Error loading products:', err);
      // Fallback to sample data if backend fails
      setProducts(getSampleProducts());
    }
  };

  // Load cart from backend
  const loadCart = async () => {
    if (!user) return;
    try {
      const response = await cartAPI.view();
      setCart(response.data.items || []);
    } catch (err) {
      console.error('Error loading cart:', err);
    }
  };

  // Load profile from backend
  const loadProfile = async () => {
    if (!user) return;
    try {
      const response = await authAPI.getProfile();
      setProfileData({
        fullName: response.data.fullName || '',
        email: response.data.email || '',
        mobile: response.data.mobile || '',
        addresses: response.data.addresses || []
      });
    } catch (err) {
      console.error('Error loading profile:', err);
    }
  };

  // Load order history
  const loadOrders = async () => {
    if (!user) return;
    try {
      const response = await ordersAPI.history();
      setOrders(response.data || []);
    } catch (err) {
      console.error('Error loading orders:', err);
    }
  };

  // Sample products fallback
  const getSampleProducts = () => [
    {
      id: 1,
      name: 'Premium Smartphone X1',
      brand: 'TechBrand',
      price: 9999,
      rating: 4.5,
      reviewCount: 1250,
      image: 'https://via.placeholder.com/300x300/3b82f6/ffffff?text=Smartphone',
      category: 'Electronics',
      description: 'Powerful processor, stunning camera, all-day battery',
      specs: '6GB RAM, 128GB Storage, 48MP Camera'
    },
    {
      id: 2,
      name: 'Wireless Earbuds Pro',
      brand: 'AudioMax',
      price: 2999,
      rating: 4.7,
      reviewCount: 890,
      image: 'https://via.placeholder.com/300x300/8b5cf6/ffffff?text=Earbuds',
      category: 'Audio',
      description: 'Active noise cancellation, 30hr battery life',
      specs: 'Bluetooth 5.2, IPX4 Water Resistant'
    },
    {
      id: 3,
      name: 'Smart Watch Ultra',
      brand: 'FitTech',
      price: 4999,
      rating: 4.3,
      reviewCount: 567,
      image: 'https://via.placeholder.com/300x300/10b981/ffffff?text=Watch',
      category: 'Wearables',
      description: 'Health tracking, GPS, always-on display',
      specs: 'Heart Rate Monitor, Sleep Tracking, 7-day Battery'
    },
    {
      id: 4,
      name: 'Gaming Laptop Z9',
      brand: 'GameForce',
      price: 59999,
      rating: 4.8,
      reviewCount: 432,
      image: 'https://via.placeholder.com/300x300/ef4444/ffffff?text=Laptop',
      category: 'Computers',
      description: 'High-performance gaming, RGB keyboard',
      specs: 'RTX 3060, 16GB RAM, 512GB SSD, 144Hz Display'
    },
    {
      id: 5,
      name: 'Camera DSLR Pro',
      brand: 'PhotoMax',
      price: 45999,
      rating: 4.6,
      reviewCount: 289,
      image: 'https://via.placeholder.com/300x300/f59e0b/ffffff?text=Camera',
      category: 'Photography',
      description: 'Professional photography, 4K video',
      specs: '24MP Sensor, Dual Card Slots, Weather Sealed'
    },
    {
      id: 6,
      name: 'Tablet Pro 11',
      brand: 'TechBrand',
      price: 34999,
      rating: 4.4,
      reviewCount: 678,
      image: 'https://via.placeholder.com/300x300/06b6d4/ffffff?text=Tablet',
      category: 'Electronics',
      description: 'Stunning display, all-day battery, perfect for work',
      specs: '11-inch Display, 128GB Storage, Stylus Support'
    }
  ];

  const handleLogin = async () => {
  if (!loginData.email || !loginData.password) {
    setError('Email and password are required');
    return;
  }

  try {
    setLoading(true);
    setError('');

    const response = await authAPI.login(
      loginData.email,
      loginData.password
    );

    // üî• VERY IMPORTANT (handles all backend formats)
    const token =
      response.data.token ||
      response.data.accessToken ||
      response.data.jwt;

    const userData =
      response.data.user ||
      response.data.data ||
      response.data;

    if (!token || !userData) {
      throw new Error('Invalid login response');
    }

    // ‚úÖ Save auth
    localStorage.setItem('token', token);
    localStorage.setItem('user', JSON.stringify(userData));

    // ‚úÖ Update state
    setUser(userData);

    // ‚úÖ Redirect
    if (userData.role?.toLowerCase() === 'admin') {
      setCurrentPage('admin');
    } else {
      setCurrentPage('products');
    }
  } catch (err) {
    console.error('LOGIN ERROR:', err);
    setError(
      err.response?.data?.message ||
      'Invalid email or password'
    );
  } finally {
    setLoading(false);
  }
};
const handleRegister = async () => {
  const { fullName, email, mobile, password, confirmPassword } = registerData;

  if (!fullName || !email || !mobile || !password) {
    setError('All fields are required');
    return;
  }

  if (password !== confirmPassword) {
    setError('Passwords do not match');
    return;
  }

  try {
    setLoading(true);
    setError('');

    const response = await authAPI.register(
      fullName,
      email,
      mobile,
      password
    );

    const token =
      response.data.token ||
      response.data.accessToken ||
      response.data.jwt;

    const userData =
      response.data.user ||
      response.data.data ||
      response.data;

    if (!token || !userData) {
      throw new Error('Invalid register response');
    }

    localStorage.setItem('token', token);
    localStorage.setItem('user', JSON.stringify(userData));

    setUser(userData);

    if (userData.role?.toLowerCase() === 'admin') {
      setCurrentPage('admin');
    } else {
      setCurrentPage('products');
    }
  } catch (err) {
    console.error('REGISTER ERROR:', err);
    setError(
      err.response?.data?.message ||
      'Registration failed'
    );
  } finally {
    setLoading(false);
  }
};


  const handleLogout = () => {
    sessionStorage.removeItem('token');
    sessionStorage.removeItem('user');
    setUser(null);
    setCurrentPage('home');
    setCart([]);
    setOrders([]);
    setChatMessages([]);
    setChatOpen(false);
  };

  const addToCart = async (product) => {
    try {
      await cartAPI.add(product.id, 1);
      await loadCart();
    } catch (err) {
      console.error('Error adding to cart:', err);
      setError('Failed to add item to cart');
    }
  };

  const updateCartQuantity = async (productId, quantity) => {
    if (quantity <= 0) {
      await removeFromCart(productId);
      return;
    }

    try {
      await cartAPI.update(productId, quantity);
      await loadCart();
    } catch (err) {
      console.error('Error updating cart:', err);
      setError('Failed to update cart');
    }
  };

  const removeFromCart = async (productId) => {
    try {
      await cartAPI.remove(productId);
      await loadCart();
    } catch (err) {
      console.error('Error removing from cart:', err);
      setError('Failed to remove item from cart');
    }
  };

  // AI Chat simulation
  const sendMessageToAgent = async (message) => {
    setIsTyping(true);

    const userMsg = { role: 'user', content: message };
    setChatMessages(prev => [...prev, userMsg]);
    setCurrentMessage('');

    setTimeout(() => {
      let agentResponse = '';
      const lowerMsg = message.toLowerCase();

      if (lowerMsg.includes('hi') || lowerMsg.includes('hello')) {
        agentResponse = "Hello! I'm your AI shopping assistant. I can help you find products, add items to cart, or answer questions about your orders. What are you looking for today?";
      } else if (lowerMsg.includes('smartphone') || lowerMsg.includes('phone')) {
        const phones = products.filter(p => p.category === 'Electronics');
        if (lowerMsg.includes('under') || lowerMsg.includes('below')) {
          agentResponse = "I found some great smartphones under ‚Çπ10,000! Here's the best option:\n\nüì± Premium Smartphone X1 by TechBrand\nPrice: ‚Çπ9,999\nRating: 4.5/5 (1,250 reviews)\nFeatures: 6GB RAM, 128GB Storage, 48MP Camera\n\nWould you like me to add this to your cart?";
        } else {
          agentResponse = `I found ${phones.length} smartphones for you! The Premium Smartphone X1 is our top pick with excellent camera and battery life. Would you like more details?`;
        }
      } else if (lowerMsg.includes('add') && lowerMsg.includes('cart')) {
        if (products.length > 0) {
          addToCart(products[0]);
          agentResponse = `Great choice! I've added ${products[0].name} to your cart. Your cart now has ${cart.length + 1} items. Would you like to continue shopping or proceed to checkout?`;
        }
      } else if (lowerMsg.includes('cart') || lowerMsg.includes('show')) {
        if (cart.length === 0) {
          agentResponse = "Your cart is currently empty. Would you like me to help you find some products?";
        } else {
          const total = cart.reduce((sum, item) => sum + (item.price * item.quantity), 0);
          agentResponse = `You have ${cart.length} items in your cart:\n\n${cart.map(item => `‚Ä¢ ${item.product?.name || item.name} - ‚Çπ${item.price} x ${item.quantity}`).join('\n')}\n\nTotal: ‚Çπ${total}\n\nReady to checkout?`;
        }
      } else if (lowerMsg.includes('recommend') || lowerMsg.includes('suggest')) {
        agentResponse = "Based on popular choices, I recommend:\n\n1. Premium Smartphone X1 - Best value for money\n2. Wireless Earbuds Pro - Great audio quality\n3. Smart Watch Ultra - Perfect for fitness tracking\n\nWhich category interests you most?";
      } else if (lowerMsg.includes('checkout') || lowerMsg.includes('buy')) {
        agentResponse = "Perfect! To proceed with checkout, I'll need to confirm your shipping address. You have saved addresses. Would you like to use them, or add a new one?";
      } else {
        agentResponse = "I can help you with:\n‚Ä¢ Finding products by category or budget\n‚Ä¢ Adding items to your cart\n‚Ä¢ Checking order status\n‚Ä¢ Product recommendations\n\nWhat would you like to do?";
      }

      const assistantMsg = { role: 'assistant', content: agentResponse };
      setChatMessages(prev => [...prev, assistantMsg]);
      setIsTyping(false);
    }, 1000 + Math.random() * 1000);
  };

  useEffect(() => {
    chatEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [chatMessages]);

  const stats = [
    { label: 'Total Sales', value: '‚Çπ2,45,890', change: '+12.5%', icon: <TrendingUp className="w-6 h-6" /> },
    { label: 'Total Orders', value: orders.length.toString(), change: '+8.2%', icon: <Package className="w-6 h-6" /> },
    { label: 'Total Customers', value: '856', change: '+15.3%', icon: <Users className="w-6 h-6" /> },
    { label: 'Total Products', value: products.length.toString(), change: '+3', icon: <ShoppingBag className="w-6 h-6" /> }
  ];

  // Landing Page
  if (!user && currentPage === 'home') {
    return (
      <div className="min-h-screen bg-gradient-to-br from-blue-50 via-purple-50 to-pink-50">
        <div className="max-w-7xl mx-auto px-4 py-12">
          <div className="text-center mb-16">
            <div className="inline-flex items-center gap-3 mb-6">
              <ShoppingBag className="w-16 h-16 text-blue-600" />
              <h1 className="text-5xl font-bold text-gray-900">ShopAI</h1>
            </div>
            <p className="text-2xl text-gray-600 mb-8">AI-Powered E-Commerce Platform</p>
            <p className="text-lg text-gray-500 max-w-2xl mx-auto mb-12">
              Experience the future of online shopping with our intelligent AI assistant
            </p>
            <div className="flex gap-4 justify-center">
              <button onClick={() => setCurrentPage('login')} className="px-8 py-4 bg-blue-600 text-white rounded-xl font-semibold hover:bg-blue-700 transition-colors shadow-lg">
                Sign In
              </button>
              <button onClick={() => setCurrentPage('register')} className="px-8 py-4 bg-white text-blue-600 rounded-xl font-semibold hover:bg-gray-50 transition-colors shadow-lg border-2 border-blue-600">
                Create Account
              </button>
            </div>
          </div>

          <div className="grid md:grid-cols-3 gap-8 mt-16">
            <div className="bg-white rounded-2xl p-8 shadow-lg">
              <div className="w-12 h-12 bg-blue-100 rounded-xl flex items-center justify-center mb-4">
                <Package className="w-6 h-6 text-blue-600" />
              </div>
              <h3 className="text-xl font-bold text-gray-900 mb-2">Smart Shopping</h3>
              <p className="text-gray-600">AI-powered product recommendations</p>
            </div>
            <div className="bg-white rounded-2xl p-8 shadow-lg">
              <div className="w-12 h-12 bg-purple-100 rounded-xl flex items-center justify-center mb-4">
                <TrendingUp className="w-6 h-6 text-purple-600" />
              </div>
              <h3 className="text-xl font-bold text-gray-900 mb-2">Best Prices</h3>
              <p className="text-gray-600">Intelligent price comparison</p>
            </div>
            <div className="bg-white rounded-2xl p-8 shadow-lg">
              <div className="w-12 h-12 bg-pink-100 rounded-xl flex items-center justify-center mb-4">
                <Settings className="w-6 h-6 text-pink-600" />
              </div>
              <h3 className="text-xl font-bold text-gray-900 mb-2">Easy Management</h3>
              <p className="text-gray-600">Seamless cart and checkout</p>
            </div>
          </div>
        </div>
      </div>
    );
  }

 /* // Login Page
  if (!user && currentPage === 'login') {
    return (
      <div className="min-h-screen bg-gradient-to-br from-blue-50 to-purple-50 flex items-center justify-center p-4">
        <div className="max-w-md w-full">
          <div className="text-center mb-8">
            <div className="inline-flex items-center gap-2 mb-2">
              <ShoppingBag className="w-10 h-10 text-blue-600" />
              <h1 className="text-3xl font-bold text-gray-900">ShopAI</h1>
            </div>
            <p className="text-gray-600">AI-Powered Shopping Platform</p>
          </div>

          <div className="bg-white rounded-2xl shadow-xl p-8">
            <h2 className="text-2xl font-bold text-gray-900 mb-2">Welcome Back</h2>
            <p className="text-gray-600 mb-6">Sign in to continue shopping</p>

            {error && (
              <div className="bg-red-50 border border-red-200 text-red-700 px-4 py-3 rounded-lg mb-4 text-sm">
                {error}
              </div>
            )}

            <div className="space-y-4">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">Email Address</label>
                <div className="relative">
                  <Mail className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 w-5 h-5" />
                  <input
                    type="email"
                    value={loginData.email}
                    onChange={(e) => setLoginData({ ...loginData, email: e.target.value })}
                    onKeyPress={(e) => e.key === 'Enter' && handleLogin()}
                    className="w-full pl-10 pr-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                    placeholder="you@example.com"
                  />
                </div>
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">Password</label>
                <div className="relative">
                  <Lock className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 w-5 h-5" />
                  <input
                    type={showPassword ? 'text' : 'password'}
                    value={loginData.password}
                    onChange={(e) => setLoginData({ ...loginData, password: e.target.value })}
                    onKeyPress={(e) => e.key === 'Enter' && handleLogin()}
                    className="w-full pl-10 pr-12 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                    placeholder="‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢"
                  />
                  <button
                    onClick={() => setShowPassword(!showPassword)}
                    className="absolute right-3 top-1/2 transform -translate-y-1/2 text-gray-400 hover:text-gray-600"
                  >
                    {showPassword ? <EyeOff className="w-5 h-5" /> : <Eye className="w-5 h-5" />}
                  </button>
                </div>
              </div>

              <button 
                onClick={handleLogin} 
                disabled={loading}
                className="w-full bg-blue-600 text-white py-3 rounded-lg font-semibold hover:bg-blue-700 transition-colors disabled:bg-blue-300"
