                            <h2 className="text-xl font-bold mb-4">Order Items</h2>
                            <div className="space-y-3">
                              {cart.map(item => (
                                <div key={item.productId} className="flex justify-between py-2 border-b">
                                  <span>{item.product?.name || item.name} x {item.quantity}</span>
                                  <span>‚Çπ{item.price * item.quantity}</span>
                                </div>
                              ))}
                            </div>
                          </div>
                        </div>

                        <div className="bg-white rounded-xl shadow-md p-6 h-fit">
                          <h3 className="text-lg font-bold mb-4">Order Total</h3>
                          <div className="space-y-3 border-b pb-4 mb-4">
                            <div className="flex justify-between text-gray-600">
                              <span>Subtotal</span>
                              <span>‚Çπ{cart.reduce((sum, item) => sum + (item.price * item.quantity), 0)}</span>
                            </div>
                          </div>
                          <div className="flex justify-between text-lg font-bold mb-6">
                            <span>Total</span>
                            <span>‚Çπ{cart.reduce((sum, item) => sum + (item.price * item.quantity), 0)}</span>
                          </div>
                          <button className="w-full bg-blue-600 text-white py-3 rounded-lg hover:bg-blue-700 font-semibold">
                            Place Order
                          </button>
                        </div>
                      </div>
                    </div>
                  </div>
                );
              }

              // Profile Page
              if (user && currentPage === 'profile') {
                return (
                  <div className="min-h-screen bg-gray-50">
                    <header className="bg-white shadow-sm">
                      <div className="max-w-7xl mx-auto px-4 py-4 flex justify-between items-center">
                        <button onClick={() => setCurrentPage('products')} className="text-gray-700 hover:text-gray-900">
                          ‚Üê Back
                        </button>
                        <h1 className="text-2xl font-bold">My Profile</h1>
                        <button onClick={handleLogout} className="bg-red-600 text-white px-4 py-2 rounded-lg hover:bg-red-700">
                          Logout
                        </button>
                      </div>
                    </header>

                    <div className="max-w-7xl mx-auto px-4 py-8">
                      <div className="grid md:grid-cols-3 gap-8">
                        <div className="md:col-span-2">
                          <div className="bg-white rounded-xl shadow-md p-6 mb-6">
                            <div className="flex justify-between items-center mb-4">
                              <h2 className="text-xl font-bold">Personal Information</h2>
                              <button onClick={() => setEditMode(!editMode)} className="text-blue-600 hover:text-blue-700 font-semibold">
                                {editMode ? 'Cancel' : 'Edit'}
                              </button>
                            </div>
                            <div className="space-y-4">
                              <div>
                                <label className="block text-sm font-medium text-gray-700 mb-1">Full Name</label>
                                <input type="text" value={profileData.fullName} disabled={!editMode} className="w-full px-4 py-2 border rounded-lg disabled:bg-gray-50" />
                              </div>
                              <div>
                                <label className="block text-sm font-medium text-gray-700 mb-1">Email</label>
                                <input type="email" value={profileData.email} disabled={!editMode} className="w-full px-4 py-2 border rounded-lg disabled:bg-gray-50" />
                              </div>
                              <div>
                                <label className="block text-sm font-medium text-gray-700 mb-1">Phone</label>
                                <input type="tel" value={profileData.mobile} disabled={!editMode} className="w-full px-4 py-2 border rounded-lg disabled:bg-gray-50" />
                              </div>
                            </div>
                          </div>

                          <div className="bg-white rounded-xl shadow-md p-6">
                            <h2 className="text-xl font-bold mb-4">Addresses</h2>
                            <div className="space-y-3">
                              {profileData.addresses?.map((addr, idx) => (
                                <div key={idx} className="p-4 border rounded-lg">
                                  <p className="font-semibold">{addr.label}</p>
                                  <p className="text-gray-600 text-sm">{addr.address}</p>
                                </div>
                              ))}
                            </div>
                          </div>
                        </div>

                        <div className="bg-white rounded-xl shadow-md p-6 h-fit">
                          <h3 className="text-lg font-bold mb-4">Quick Stats</h3>
                          <div className="space-y-4">
                            <div>
                              <p className="text-gray-600 text-sm">Total Orders</p>
                              <p className="text-2xl font-bold">{orders.length}</p>
                            </div>
                            <div>
                              <p className="text-gray-600 text-sm">Member Since</p>
                              <p className="text-sm font-semibold">2024</p>
                            </div>
                          </div>
                        </div>
                      </div>
                    </div>
                  </div>
                );
              }

              // Admin Dashboard
              if (user && user.role === 'Admin' && currentPage === 'admin') {
                return (
                  <div className="min-h-screen bg-gray-50">
                    <header className="bg-white shadow-sm">
                      <div className="max-w-7xl mx-auto px-4 py-4 flex justify-between items-center">
                        <div className="flex items-center gap-3">
                          <BarChart3 className="w-8 h-8 text-blue-600" />
                          <h1 className="text-2xl font-bold">Admin Dashboard</h1>
                        </div>
                        <button onClick={handleLogout} className="bg-red-600 text-white px-4 py-2 rounded-lg hover:bg-red-700">
                          Logout
                        </button>
                      </div>
                    </header>

                    <div className="max-w-7xl mx-auto px-4 py-8">
                      <div className="grid md:grid-cols-4 gap-6 mb-8">
                        {stats.map((stat, idx) => (
                          <div key={idx} className="bg-white rounded-xl shadow-md p-6">
                            <div className="flex justify-between items-start mb-4">
                              <div>
                                <p className="text-gray-600 text-sm">{stat.label}</p>
                                <p className="text-3xl font-bold mt-2">{stat.value}</p>
                              </div>
                              <div className="text-blue-600">{stat.icon}</div>
                            </div>
                            <p className="text-green-600 text-sm font-semibold">{stat.change}</p>
                          </div>
                        ))}
                      </div>

                      <div className="bg-white rounded-xl shadow-md p-6">
                        <h2 className="text-xl font-bold mb-6">Recent Orders</h2>
                        <div className="overflow-x-auto">
                          <table className="w-full">
                            <thead>
                              <tr className="border-b">
                                <th className="text-left py-3 px-4 font-semibold">Order ID</th>
                                <th className="text-left py-3 px-4 font-semibold">Date</th>
                                <th className="text-left py-3 px-4 font-semibold">Amount</th>
                                <th className="text-left py-3 px-4 font-semibold">Status</th>
                              </tr>
                            </thead>
                            <tbody>
                              {orders.slice(0, 5).map(order => (
                                <tr key={order.id} className="border-b hover:bg-gray-50">
                                  <td className="py-3 px-4">#{order.id}</td>
                                  <td className="py-3 px-4">{new Date(order.createdAt).toLocaleDateString()}</td>
                                  <td className="py-3 px-4">‚Çπ{order.totalAmount}</td>
                                  <td className="py-3 px-4"><span className="bg-green-100 text-green-700 px-3 py-1 rounded-full text-sm">Completed</span></td>
                                </tr>
                              ))}
                            </tbody>
                          </table>
                        </div>
                      </div>
                    </div>
                  </div>
                );
              }

              return null;
            };

            export default CompleteIntegratedApp;
