                                      const paymentMethod = pmEl ? pmEl.value : 'COD';

                                      // prepare items payload
                                      const items = (cart || []).map(it => ({
                                        productId: it.productId || it.product?.id || it.id,
                                        quantity: it.quantity || 1
                                      }));

