    {/* Add Address Button */}
    <button
      onClick={async () => {
        try {
          const line1 = window.prompt('Address Line 1');
          if (!line1) return;

          const payload = {
            AddressLine1: line1,
            AddressLine2: window.prompt('Address Line 2 (optional)') || '',
            City: window.prompt('City') || '',
            State: window.prompt('State (optional)') || '',
            PostalCode: window.prompt('Postal Code (optional)') || '',
            Country: window.prompt('Country', 'India') || 'India',
            IsDefault: window.confirm('Set as default address?')
          };

          // Determine current session user (if any)
          let sessionUser = null;
          try {
            sessionUser = JSON.parse(sessionStorage.getItem('user') || 'null');
          } catch (e) {
            sessionUser = null;
          }

          // If logged in, attach owner identifier so server can associate the address
          if (sessionUser) {
            if (sessionUser.id) payload.UserId = sessionUser.id;
            else if (sessionUser.userId) payload.UserId = sessionUser.userId;
            else if (sessionUser.email) payload.OwnerEmail = sessionUser.email;
            // Use API if available
            if (typeof addressAPI?.add === 'function') {
              await addressAPI.add(payload);
            } else {
              // fallback to local storage if API not available
              const local = JSON.parse(localStorage.getItem('local_addresses') || '[]');
              payload.id = Date.now();
              local.push(payload);
              localStorage.setItem('savedAddresses', JSON.stringify(local));
            }
          } else {
            // Not logged in â†’ persist locally so addresses remain after logout
            const local = JSON.parse(localStorage.getItem('local_addresses') || '[]');
            payload.id = Date.now();
            local.push(payload);
            localStorage.setItem('savedAddresses', JSON.stringify(local));
          }


          // Refresh address list
          await fetchAddresses();

          alert('Address added successfully');
        } catch (err) {
          console.error(err);
          alert(err?.response?.data?.message || err.message || 'Failed to add address');
        }
      }}
      className="px-4 py-2 bg-green-600 text-white rounded-lg hover:bg-green-700"
    >
      Add Address
    </button>

    {/* Save All Addresses Button */}
    <button
      onClick={async () => {
        try {
          if (!profileData.addresses || !profileData.addresses.length) {
            alert('No addresses to save');
            return;
          }

          let sessionUser = null;
          try {
            sessionUser = JSON.parse(sessionStorage.getItem('user') || 'null');
          } catch (e) {
            sessionUser = null;
          }

          if (sessionUser && typeof addressAPI?.saveAll === 'function') {
            // Attach owner info where missing so server can persist ownership
            const toSave = (profileData.addresses || []).map(a => {
              const copy = { ...a };
              if (!copy.UserId && sessionUser.id) copy.UserId = sessionUser.id;
              if (!copy.OwnerEmail && sessionUser.email) copy.OwnerEmail = sessionUser.email;
              return copy;
            });
            await addressAPI.saveAll(toSave);
          } else {
            // When logged out or API missing, save locally
            localStorage.setItem('savedAddresses', JSON.stringify(profileData.addresses || []));
          }

          // Refresh address list
          await fetchAddresses();

          alert('All addresses saved');
        } catch (err) {
          console.error(err);
          alert(err?.response?.data?.message || err.message || 'Failed to save addresses');
        }
      }}
      className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700"
    >
      Save Addresses

    </button>
  </div>

  {/* Address List */}
  {(() => {
    // Determine session user
    let sessionUser = null;
    try {
      sessionUser = JSON.parse(sessionStorage.getItem('user') || 'null');
    } catch (e) {
      sessionUser = null;
    }

    // Source addresses: profileData.addresses is maintained by fetchAddresses which already tries server then local
    const allAddrs = Array.isArray(profileData.addresses) ? profileData.addresses : [];

    // If server returned all addresses table, attempt to filter by owner properties.
    // If filtering yields no results, fall back to showing allAddrs (server often returns only the user's addresses).
    const ownerId = sessionUser?.id ?? sessionUser?.userId ?? null;
    const ownerEmail = sessionUser?.email ?? null;

    const filtered = allAddrs.filter(a =>
      a.UserId === ownerId ||
      a.userId === ownerId ||
      (a.OwnerEmail && ownerEmail && a.OwnerEmail.toLowerCase() === ownerEmail.toLowerCase()) ||
      (a.email && ownerEmail && a.email.toLowerCase() === ownerEmail.toLowerCase()) ||
      (a.userEmail && ownerEmail && a.userEmail.toLowerCase() === ownerEmail.toLowerCase())
    );

    const displayed = sessionUser ? (filtered.length > 0 ? filtered : allAddrs) : allAddrs;

    if (!displayed || displayed.length === 0) {
      return <p className="text-sm text-gray-500">No addresses found</p>;
    }

    return displayed.map((addr) => (
      <div key={addr.id ?? addr.addressId ?? addr.AddressId ?? JSON.stringify(addr)} className="border rounded-lg p-4 mb-3">
