              <div>
                <h1 className="text-xl font-bold text-gray-900">Admin Dashboard</h1>
                <p className="text-sm text-gray-600">Welcome, {user.name}</p>
              </div>
            </div>
            <div className="flex items-center gap-4">
              <div className="text-right">
                <p className="text-sm font-medium text-gray-900">{user.name}</p>
                <p className="text-xs text-gray-600">{user.role}</p>
              </div>
              <button 
                onClick={handleLogout} 
                className="px-4 py-2 text-sm text-red-600 hover:bg-red-50 rounded-lg flex items-center gap-2"
              >
                <LogOut className="w-4 h-4" />
                Logout
              </button>
            </div>
          </div>
        </div>
      </header>

      {/* TABS */}
      <div className="bg-white border-b sticky top-16 z-30">
        <div className="max-w-7xl mx-auto px-4 flex gap-8 overflow-x-auto">
          <button
            onClick={() => setActiveTab('overview')}
            className={`py-4 px-2 border-b-2 font-medium transition-colors whitespace-nowrap ${
              activeTab === 'overview'
                ? 'border-blue-600 text-blue-600'
                : 'border-transparent text-gray-600 hover:text-gray-900'
            }`}
          >
            Overview
          </button>
          <button
            onClick={() => setActiveTab('customers')}
            className={`py-4 px-2 border-b-2 font-medium transition-colors whitespace-nowrap ${
              activeTab === 'customers'
                ? 'border-blue-600 text-blue-600'
                : 'border-transparent text-gray-600 hover:text-gray-900'
            }`}
          >
            Customers
          </button>
          <button
            onClick={() => setActiveTab('orders')}
            className={`py-4 px-2 border-b-2 font-medium transition-colors whitespace-nowrap ${
              activeTab === 'orders'
                ? 'border-blue-600 text-blue-600'
                : 'border-transparent text-gray-600 hover:text-gray-900'
            }`}
          >
            Orders
          </button>
        <button
          onClick={() => setActiveTab('products')}
          className={`py-4 px-2 border-b-2 font-medium transition-colors whitespace-nowrap ${
            activeTab === 'products'
              ? 'border-blue-600 text-blue-600'
              : 'border-transparent text-gray-600 hover:text-gray-900'
          }`}
        >
          Products
        </button>
        </div>
      </div>

      {/* CONTENT */}
      <div className="max-w-7xl mx-auto px-4 py-8">
        {/* Reuse top-level error if present */}
        {error && (
          <div className="bg-red-50 border border-red-200 text-red-700 px-4 py-3 rounded-lg mb-6">
            {error}
          </div>
        )}

        {/* OVERVIEW TAB */}
              {activeTab === 'overview' && (
              <div>
                <h2 className="text-2xl font-bold mb-6">Dashboard Overview</h2>
                <div className="grid md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
                {(adminStats || []).map((stat, idx) => (
              <div key={idx} className="bg-white rounded-xl shadow-md p-6">
                <p className="text-gray-600 text-sm mb-1">{stat.label}</p>
                <p className="text-2xl font-bold text-gray-900">{stat.value}</p>
              </div>
                ))}
                </div>
              </div>
              )}

              {/* CUSTOMERS TAB */}
              {activeTab === 'customers' && (
                <div>
                  <h2 className="text-2xl font-bold mb-6">Registered Customers</h2>

                  <div className="overflow-x-auto">
                    <table className="w-full text-sm">
                      <thead className="border-b">
                        <tr>
                          <th className="text-left py-3 font-semibold text-gray-700">Name</th>
                          <th className="text-left py-3 font-semibold text-gray-700">Email</th>
                          <th className="text-left py-3 font-semibold text-gray-700">Mobile</th>
                          <th className="text-left py-3 font-semibold text-gray-700">Role</th>
                          <th className="text-left py-3 font-semibold text-gray-700">Status</th>
                        </tr>
                      </thead>

                      <tbody>
                        {(() => {
                          // âœ… SINGLE SOURCE OF TRUTH
                          const users = Array.isArray(adminCustomers) ? adminCustomers : [];

                          if (users.length === 0) {
                            return (
                              <tr>
                                <td
                                  colSpan="5"
                                  className="py-8 text-center text-gray-500"
                                >
                                  No customers found
                                </td>
                              </tr>
                            );
                          }

                          return users.map((u, idx) => {
                            const profile = u.profile || u;

                            const id = u.id || u._id || idx;

                            const name =
                              profile.fullName ||
                              `${profile.firstName || ''} ${profile.lastName || ''}`.trim() ||
                              'N/A';

                            const email = profile.email || 'N/A';
                            const mobile = profile.mobile || profile.phone || 'N/A';

                            const role =
                              u.role === 1 || u.role === 'Admin'
                                ? 'Admin'
                                : 'Customer';

                            const status =
                              u.isActive === false ? 'Inactive' : 'Active';

                            return (
                              <tr key={id} className="border-b hover:bg-gray-50">
                                <td className="py-3 font-medium">{name}</td>
                                <td className="py-3 text-gray-600">{email}</td>
                                <td className="py-3 text-gray-600">{mobile}</td>

                                <td className="py-3">
                                  <span className="px-3 py-1 bg-blue-100 text-blue-700 rounded-full text-xs font-semibold">
                                    {role}
                                  </span>
                                </td>

                                <td className="py-3">
                                  <span
                                    className={`px-3 py-1 rounded-full text-xs font-semibold ${
                                      status === 'Active'
                                        ? 'bg-green-100 text-green-700'
                                        : 'bg-yellow-100 text-yellow-700'
                                    }`}
                                  >
                                    {status}
                                  </span>
                                </td>
                              </tr>
                            );
                          });
                        })()}
                      </tbody>
                    </table>
                  </div>
                </div>
              )}

              {/* Products TAB */}
          {activeTab === 'products' && (

            <div>
              {/* admin product actions (create / edit / delete) */}
              <div className="flex items-center justify-between mb-6">
                <h2 className="text-2xl font-bold">Products</h2>

               <div className="flex items-center space-x-2">
  <input
    type="text"
    value={searchQuery}
    onChange={(e) => setSearchQuery(e.target.value)}
    placeholder="Search products by name, brand or category..."
    className="px-3 py-2 border rounded-lg w-72 focus:ring-2 focus:ring-blue-500"
  />

 <button
  onClick={async () => {
    if (!searchQuery.trim()) return; // do nothing if empty
    setLoading(true);
    try {
      const res = await productsAPI.search(searchQuery.trim());
      const productData = Array.isArray(res.data)
        ? res.data
        : res.data?.data || res.data?.products || [];
      setProducts(productData);
      setError('');
    } catch (err) {
      console.error('Search failed', err);
      setError(err.response?.data?.message || err.message || 'Search failed');
    } finally {
      setLoading(false);
    }
  }}
  className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700"
>
  {loading ? 'Searching...' : 'Search'}
</button>

</div>

              </div>
            {/* helper functions */}
            <script type="text/javascript" dangerouslySetInnerHTML={{__html: ""}} />

            {(() => {
              // define functions inside IIFE so they are in component scope
              const createProduct = async () => {
                try {
                  setLoading(true);
                  const name = window.prompt('Product name:');
                  if (!name) return;
                  const brand = window.prompt('Brand:', 'BrandName') || '';
                  const category = window.prompt('Category:', 'General') || '';
                  const priceRaw = window.prompt('Price (number):', '0');
                  const price = Number(priceRaw || 0);
                  const image = window.prompt('Image URL:', 'https://via.placeholder.com/300') || '';
                  const description = window.prompt('Short description:', '') || '';
                  const specs = window.prompt('Specs (comma separated):', '') || '';

                  // ask for stock quantity (was referenced but not defined)
                  const stockRaw = window.prompt('Stock quantity (number):', '0');
                  const stock = Number(stockRaw || 0);
                  const categoryOptions = categories.map(c => `${c.id}: ${c.name}`).join('\n');
                  const categoryIdRaw = window.prompt(
                    `Select category by ID:\n${categoryOptions}`,
                    categories[0]?.id || 1
                  );
                  const categoryId = Number(categoryIdRaw);
                  if (!categoryId) return alert('Category is required');

                  // send category (string) and defined stockQuantity to backend payload
                  const payload = {
                    name,
                    description,
                    price,
                    categoryId, // <-- must be number
                    imageUrl: image,
                    stockQuantity: stock,
                    brand,
                    specifications: specs
                  };


                  const res = await adminAPI.createProduct(payload);
                  // refresh products after create
                  await loadProducts();
                  setError('');
                  alert('Product created successfully');
                } catch (err) {
                  console.error('Create product failed', err);
                  setError(err.response?.data?.message || err.message || 'Create product failed');
                } finally {
                  setLoading(false);
                }
              };

              const editProduct = async (product) => {
                try {
                  setLoading(true);
                  const name = window.prompt('Product name:', product.name) || product.name;
                  const brand = window.prompt('Brand:', product.brand || '') || product.brand;
                  const category = window.prompt('Category:', product.category || '') || product.category;
                  const priceRaw = window.prompt('Price (number):', (product.price || 0).toString());
                  const price = Number(priceRaw || product.price || 0);
                  const image = window.prompt('Image URL:', product.image || '') || product.image;
                  const description = window.prompt('Short description:', product.description || '') || product.description;
                  const specs = window.prompt('Specs (comma separated):', product.specs || '') || product.specs;

                    // prompt for updated stock (fallback to existing product.stockQuantity)
                    const stockRaw = window.prompt('Stock quantity (number):', (product.stockQuantity || 0).toString());
                    const stock = stockRaw === null ? (product.stockQuantity || 0) : Number(stockRaw || product.stockQuantity || 0);
                  const categoryOptions = categories.map(c => `${c.id}: ${c.name}`).join('\n');
                  const categoryIdRaw = window.prompt(
                    `Select category by ID:\n${categoryOptions}`,
                    categories[0]?.id || 1
                  );
                  const categoryId = Number(categoryIdRaw);
                  if (!categoryId) return alert('Category is required');

                    const payload = {
                    name,
                    description,
                    price,
                    categoryId, // <-- must be number
                    imageUrl: image,
                    stockQuantity: stock,
                    brand,
                    specifications: specs
                  };

                  await adminAPI.updateProduct(product.id, payload);
                  // optimistic update by reloading products
                  await loadProducts();
                  setError('');
                  alert('Product updated successfully');
                } catch (err) {
                  console.error('Update product failed', err);
                  setError(err.response?.data?.message || err.message || 'Update product failed');
                } finally {
                  setLoading(false);
                }
              };

              const deleteProduct = async (product) => {
                try {
                  const ok = window.confirm(`Delete product "${product.name}"? This cannot be undone.`);
                  if (!ok) return;
                  setLoading(true);
                  await adminAPI.deleteProduct(product.id);
                  // remove locally for immediate feedback
                  setProducts(prev => (prev || []).filter(p => p.id !== product.id));
                  // attempt to refresh list
                  try { await loadProducts(); } catch (_) {}
                  setError('');
                  alert('Product deleted');
                } catch (err) {
                  console.error('Delete product failed', err);
                  setError(err.response?.data?.message || err.message || 'Delete product failed');
                } finally {
                  setLoading(false);
                }
              };

              // render JSX
              return (
                <div>
                  <div className="flex items-center justify-between mb-6">

                    <button
                      onClick={createProduct}
                      className="px-4 py-2 bg-green-600 text-white rounded-lg hover:bg-green-700"
                    >
                      {loading ? 'Processing...' : 'Create Product'}
                    </button>
                  </div>
                  <div className="mb-4 flex justify-end">
                    <button
                      onClick={() => setCurrentPage('products')}
                      className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700"
                    >
                      View Store
                    </button>
                  </div>
                  {(products || []).length === 0 ? (
                    <p className="text-sm text-gray-500">No products available</p>
                  ) : (
                    <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-6">
                      {(products || []).map((p) => (
                        <div key={p.id} className="bg-white rounded-xl shadow-md overflow-hidden">
                          <img src={p.image} alt={p.name} className="w-full h-40 object-cover" />
                          <div className="p-4">
                            <div className="flex items-start justify-between gap-4">
                              <div>
                                <p className="text-sm text-gray-500">{p.brand || p.category}</p>
                                <h3 className="text-lg font-bold text-gray-900">{p.name}</h3>
                              </div>
                              <div className="text-right">
                                <p className="text-xl font-bold text-blue-600">â‚¹{p.price}</p>
                                <p className="text-sm text-gray-600">{p.rating ? `${p.rating} â˜…` : ''}</p>
                              </div>
                            </div>

                            <p className="text-sm text-gray-600 mt-3 line-clamp-3">{p.description}</p>

                            <div className="flex items-center justify-between mt-4">
                              <div className="flex gap-2">
                                <button
                                  onClick={() => editProduct(p)}
                                  className="px-3 py-1 bg-yellow-100 text-yellow-700 rounded"
                                >
                                  Edit
                                </button>
                                <button
                                  onClick={() => deleteProduct(p)}
                                  className="px-3 py-1 bg-red-100 text-red-700 rounded"
                                >
                                  Delete
                                </button>
                              </div>
                              <div className="text-sm text-gray-500">{p.specs || ''}</div>
                            </div>
                          </div>
                        </div>
                      ))}
                    </div>
                  )}
                </div>
              );
            })()}
          </div>
        )}
        {/* ORDERS TAB */}
        {activeTab === 'orders' && (
          <div>
            <h2 className="text-2xl font-bold mb-6">Recent Orders</h2>
            <div className="overflow-x-auto">
              <table className="w-full text-sm">
                <thead className="border-b">
                  <tr>
                    <th className="text-left py-3 font-semibold text-gray-700">Order ID</th>
                    <th className="text-left py-3 font-semibold text-gray-700">Date</th>
                    <th className="text-left py-3 font-semibold text-gray-700">Amount</th>
                    <th className="text-left py-3 font-semibold text-gray-700">Status</th>
                  </tr>
                </thead>
                <tbody>
                  {(adminOrders || []).length === 0 ? (
                    <tr>
                      <td colSpan="4" className="py-8 text-center text-gray-500">
                        No orders yet
                      </td>
                    </tr>
                  ) : (
                    (adminOrders || []).map((order) => (
                      <tr key={order.id} className="border-b hover:bg-gray-50">
                        <td className="py-3 font-medium">#{order.id}</td>
                        <td className="py-3 text-gray-600">{order.createdAt ? new Date(order.createdAt).toLocaleDateString() : ''}</td>
                        <td className="py-3 font-semibold">â‚¹{(order.totalAmount || 0).toLocaleString()}</td>
                        <td className="py-3">
                          <span className={`px-3 py-1 rounded-full text-xs font-semibold ${order.status === 'Pending' ? 'bg-yellow-100 text-yellow-700' : 'bg-green-100 text-green-700'}`}>
                            {order.status || 'N/A'}
                          </span>
                        </td>
                      </tr>
                    ))
                  )}
                </tbody>
              </table>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}
}                                                                                             
export default CompleteIntegratedApp;
