import React, { useEffect, useState, useRef } from "react";
import {
  authAPI,
  productsAPI,
  cartAPI,
  ordersAPI
} from "./api";

import {
  ShoppingBag,
  ShoppingCart,
  LogOut,
  UserCircle,
  MessageCircle,
  Send
} from "lucide-react";

const App = () => {
  const [user, setUser] = useState(null);
  const [page, setPage] = useState("login");
  const [products, setProducts] = useState([]);
  const [cart, setCart] = useState([]);
  const [error, setError] = useState("");

  // Auth state
  const [loginData, setLoginData] = useState({ email: "", password: "" });
  const [registerData, setRegisterData] = useState({
    fullName: "",
    email: "",
    mobile: "",
    password: ""
  });

  // AI chat (frontend only)
  const [chatOpen, setChatOpen] = useState(false);
  const [chatMessages, setChatMessages] = useState([]);
  const chatEndRef = useRef(null);

  /* ===========================
     AUTO LOGIN (JWT)
  ============================ */
  useEffect(() => {
    const loadProfile = async () => {
      try {
        const res = await authAPI.getProfile();
        setUser(res.data);
        setPage("products");
      } catch {
        localStorage.removeItem("token");
      }
    };

    if (localStorage.getItem("token")) {
      loadProfile();
    }
  }, []);

  /* ===========================
     LOAD PRODUCTS
  ============================ */
  useEffect(() => {
    if (!user) return;

    const loadProducts = async () => {
      const res = await productsAPI.getAll();
      setProducts(res.data);
    };

    const loadCart = async () => {
      const res = await cartAPI.view();
      setCart(res.data);
    };

    loadProducts();
    loadCart();
  }, [user]);

  /* ===========================
     AUTH
  ============================ */
  const login = async () => {
    try {
      const res = await authAPI.login(
        loginData.email,
        loginData.password
      );

      localStorage.setItem("token", res.data.token);
      setUser(res.data.user);
      setPage("products");
    } catch (err) {
      setError(err.response?.data?.message || "Login failed");
    }
  };

  const register = async () => {
    try {
      await authAPI.register(
        registerData.fullName,
        registerData.email,
        registerData.mobile,
        registerData.password
      );
      setPage("login");
    } catch (err) {
      setError(err.response?.data?.message || "Registration failed");
    }
  };

  const logout = () => {
    localStorage.removeItem("token");
    setUser(null);
    setCart([]);
    setPage("login");
  };

  /* ===========================
     CART
  ============================ */
  const addToCart = async (id) => {
    await cartAPI.add(id, 1);
    const res = await cartAPI.view();
    setCart(res.data);
  };

  const removeFromCart = async (id) => {
    await cartAPI.remove(id);
    const res = await cartAPI.view();
    setCart(res.data);
  };

  /* ===========================
     CHAT (UI ONLY)
  ============================ */
  const sendMessage = (msg) => {
    setChatMessages((prev) => [
      ...prev,
      { role: "user", text: msg },
      {
        role: "assistant",
        text:
          "I analyze products using backend data and recommend the best options."
      }
    ]);
  };

  useEffect(() => {
    chatEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [chatMessages]);

  /* ===========================
     UI
  ============================ */

  if (!user && page === "login") {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="w-96 bg-white p-6 shadow rounded">
          <h2 className="text-xl font-bold mb-4">Login</h2>
          {error && <p className="text-red-500">{error}</p>}
          <input
            className="border p-2 w-full mb-2"
            placeholder="Email"
            onChange={(e) =>
              setLoginData({ ...loginData, email: e.target.value })
            }
          />
          <input
            type="password"
            className="border p-2 w-full mb-4"
            placeholder="Password"
            onChange={(e) =>
              setLoginData({ ...loginData, password: e.target.value })
            }
          />
          <button onClick={login} className="bg-blue-600 text-white w-full p-2">
            Login
          </button>
          <p
            className="text-sm mt-3 text-blue-600 cursor-pointer"
            onClick={() => setPage("register")}
          >
            Create account
          </p>
        </div>
      </div>
    );
  }

  if (!user && page === "register") {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="w-96 bg-white p-6 shadow rounded">
          <h2 className="text-xl font-bold mb-4">Register</h2>
          <input
            className="border p-2 w-full mb-2"
            placeholder="Full Name"
            onChange={(e) =>
              setRegisterData({ ...registerData, fullName: e.target.value })
            }
          />
          <input
            className="border p-2 w-full mb-2"
            placeholder="Email"
            onChange={(e) =>
              setRegisterData({ ...registerData, email: e.target.value })
            }
          />
          <input
            className="border p-2 w-full mb-2"
            placeholder="Mobile"
            onChange={(e) =>
              setRegisterData({ ...registerData, mobile: e.target.value })
            }
          />
          <input
            type="password"
            className="border p-2 w-full mb-4"
            placeholder="Password"
            onChange={(e) =>
              setRegisterData({ ...registerData, password: e.target.value })
            }
          />
          <button
            onClick={register}
            className="bg-green-600 text-white w-full p-2"
          >
            Register
          </button>
        </div>
      </div>
    );
  }

  /* ===========================
     PRODUCTS PAGE
  ============================ */
  return (
    <div className="min-h-screen bg-gray-50">
      <header className="bg-white shadow p-4 flex justify-between">
        <div className="flex gap-2 items-center">
          <ShoppingBag />
          <h1 className="font-bold">ShopAI</h1>
        </div>
        <div className="flex gap-4 items-center">
          <button onClick={() => setPage("cart")}>
            <ShoppingCart /> {cart.length}
          </button>
          <UserCircle />
          <LogOut onClick={logout} className="cursor-pointer text-red-600" />
        </div>
      </header>

      <main className="p-6 grid grid-cols-1 md:grid-cols-3 gap-6">
        {products.map((p) => (
          <div key={p.id} className="bg-white p-4 rounded shadow">
            <h3 className="font-bold">{p.name}</h3>
            <p>â‚¹{p.price}</p>
            <button
              onClick={() => addToCart(p.id)}
              className="mt-2 bg-blue-600 text-white px-4 py-1 rounded"
            >
              Add to Cart
            </button>
          </div>
        ))}
      </main>

      {chatOpen && (
        <div className="fixed bottom-4 right-4 bg-white w-80 shadow-lg p-3">
          {chatMessages.map((m, i) => (
            <p key={i} className="text-sm">
              <b>{m.role}:</b> {m.text}
            </p>
          ))}
          <input
            className="border p-1 w-full mt-2"
            placeholder="Ask AI..."
            onKeyDown={(e) =>
              e.key === "Enter" && sendMessage(e.target.value)
            }
          />
          <div ref={chatEndRef} />
        </div>
      )}

      <button
        onClick={() => setChatOpen(!chatOpen)}
        className="fixed bottom-6 right-6 bg-blue-600 text-white p-3 rounded-full"
      >
        <MessageCircle />
      </button>
    </div>
  );
};

export default App;
