import axios from 'axios';

const API_BASE_URL = 'http://localhost:5033/api';

// Create axios instance
const api = axios.create({
  baseURL: API_BASE_URL,
  headers: {
    'Content-Type': 'application/json',
    'Accept': 'application/json'
  }
});

// ===================== ADDRESS API =====================
const LOCAL_ADDR_KEY = 'local_addresses';

function getLocalAddresses() {
  try {
    const legacy = localStorage.getItem('addresses');
    if (legacy) {
      const parsed = JSON.parse(legacy);
      if (Array.isArray(parsed) && parsed.length) setLocalAddresses(parsed);
      localStorage.removeItem('addresses');
    }
  } catch (e) {}
  try {
    const raw = localStorage.getItem(LOCAL_ADDR_KEY);
    if (!raw) {
      setLocalAddresses([]);
      return [];
    }
    const parsed = JSON.parse(raw);
    if (Array.isArray(parsed)) return parsed;
    setLocalAddresses([]);
    return [];
  } catch (e) {
    return [];
  }
}

function setLocalAddresses(addrs) {
  try {
    localStorage.setItem(LOCAL_ADDR_KEY, JSON.stringify(addrs));
  } catch (e) {}
}

function upsertLocalAddress(addr) {
  const list = getLocalAddresses();
  const idx = list.findIndex(a => a.id === addr.id);
  if (idx >= 0) list[idx] = addr;
  else list.push(addr);
  setLocalAddresses(list);
}

function removeLocalAddress(id) {
  setLocalAddresses(getLocalAddresses().filter(a => a.id !== id));
}

// Sync local addresses to server
export async function syncLocalAddresses() {
  const local = getLocalAddresses();
  if (!local || !local.length) return;
  try {
    const res = await api.get('/user/addresses');
    const server = Array.isArray(res.data) ? res.data : [];
    const serverIds = new Set(server.map(a => a.id));
    for (const addr of local) {
      if (!serverIds.has(addr.id)) {
        const payload = { ...addr };
        delete payload.id;
        try {
          const r = await api.post('/user/addresses', payload);
          if (r && r.data) {
            removeLocalAddress(addr.id);
            upsertLocalAddress(r.data);
          }
        } catch (e) {}
      }
    }
  } catch (e) {}
}

export function clearLocalAddresses() {
  setLocalAddresses([]);
}

export function getLocalAddressById(id) {
  return getLocalAddresses().find(a => a.id === id) || null;
}

// ===================== addressAPI =====================
export const addressAPI = {
  getAll: () =>
    api.get('/user/addresses')
      .catch(() => Promise.resolve({ data: getLocalAddresses() })),

  add: (address) =>
    api.post('/user/addresses', address)
      .then(res => {
        if (res && res.data) upsertLocalAddress(res.data);
        return res;
      })
      .catch(() => {
        const local = { ...address, id: Date.now() };
        upsertLocalAddress(local);
        return Promise.resolve({ data: local, __local: true });
      }),

  update: (id, address) =>
    api.put(`/user/addresses/${id}`, address)
      .then(res => {
        if (res && res.data) upsertLocalAddress(res.data);
        return res;
      })
      .catch(() => {
        const local = { ...address, id };
        upsertLocalAddress(local);
        return Promise.resolve({ data: local, __local: true });
      }),

  remove: (id) =>
    api.delete(`/user/addresses/${id}`)
      .then(res => {
        removeLocalAddress(id);
        return res;
      })
      .catch(() => {
        removeLocalAddress(id);
        return Promise.resolve({ data: { id }, __local: true });
      }),

  saveAll: async (addresses) => {
    // fallback: iterate if bulk API not available
    const results = [];
    for (const a of addresses) {
      if (a.id) {
        try {
          const res = await addressAPI.update(a.id, a);
          results.push(res.data);
        } catch (e) {
          results.push(a);
        }
      } else {
        try {
          const res = await addressAPI.add(a);
          results.push(res.data);
        } catch (e) {
          results.push(a);
        }
      }
    }
    return results;
  },

  // ✅ Add syncLocalAddresses here
  syncLocalAddresses: async () => {
    const local = getLocalAddresses();
    if (!local || !local.length) return;
    try {
      const res = await api.get('/user/addresses');
      const server = Array.isArray(res.data) ? res.data : [];
      const serverIds = new Set(server.map(a => a.id));
      for (const addr of local) {
        if (!serverIds.has(addr.id)) {
          const payload = { ...addr };
          delete payload.id;
          try {
            const r = await api.post('/user/addresses', payload);
            if (r && r.data) {
              removeLocalAddress(addr.id);
              upsertLocalAddress(r.data);
            }
          } catch (e) {
            // ignore individual address upload failures
          }
        }
      }
    } catch (e) {
      // unable to fetch server addresses; keep local data
    }
  }
};


// ===================== Attach JWT token =====================
api.interceptors.request.use(
  (config) => {
    const token = sessionStorage.getItem('token') || localStorage.getItem('token');
    if (token) {
      config.headers = config.headers || {};
      config.headers.Authorization = `Bearer ${token}`;
    }
    return config;
  },
  (error) => Promise.reject(error)
);

// ===================== AUTH API =====================
export const authAPI = {
  login: (email, password) => api.post('/auth/login', { email, password }),
  register: (fullName, email, mobile, password) => api.post('/auth/register', { fullName, email, mobile, password }),
  getProfile: () => api.get('/user/profile')
};

// ===================== PRODUCTS API =====================
export const productsAPI = {
  getAll: () => api.get('/products'),
  search: (query) => api.get('/products/search', { params: { query: query || '' } }),
  getById: (id) => api.get(`/products/${id}`)
};

// ===================== CART API =====================
export const cartAPI = {
  view: () => api.get('/cart/view'),
  add: (data) => api.post('/cart/add', data),
  remove: (productId) => api.delete(`/cart/remove/${productId}`),
  update: (productId, quantity) => api.put('/cart/update', { productId, quantity }),
  clear: () => api.delete('/cart/clear')
};

// ===================== ORDERS API =====================
export const ordersAPI = {
  confirm: (shippingAddressId) => api.post('/order/confirm', { shippingAddressId }),
  history: () => api.get('/order/history'),
  getById: (id) => api.get(`/order/${id}`)
};

// ===================== RECOMMENDATIONS API =====================
export const recommendationsAPI = {
  rank: (productIds, criteria) => api.post('/recommendations/rank', { productIds, criteria })
};

// ===================== PAYMENT API =====================
export const paymentAPI = {
  initiate: (orderId, amount, paymentMethod) => api.post('/payment/initiate', { orderId, amount, paymentMethod }),
  verify: (paymentId, transactionId) => api.post('/payment/verify', { paymentId, transactionId }),
  getStatus: (paymentId) => api.get(`/payment/status/${paymentId}`)
};

// ===================== INVENTORY API =====================
export const inventoryAPI = {
  getStock: (productId) => api.get(`/inventory/stock/${productId}`),
  checkAvailability: (productId, quantity) => api.get('/inventory/check', { params: { productId, quantity } })
};

// ===================== ADMIN API =====================
export function fetchUserDetails(userId) {
  return api.get(`/admin/users/${userId}`).then(r => r.data);
}

export const adminAPI = {
  getDashboard: () => api.get('/admin/dashboard'),
  getUsers: () => api.get('/admin/users'),
  placeOrder: (orderData) => api.post('/order/place', orderData),
  getOrders: () => api.get('/admin/orders'),
  createProduct: (data) => api.post('/products', data),
  updateProduct: (id, data) => api.put(`/products/${id}`, data),
  deleteProduct: (id) => api.delete(`/products/${id}`),
  fetchUserDetails
};

export default api;
