const handleLogin = async () => {
  setError('');
  setLoading(true);

  try {
    const response = await authAPI.login(
      loginData.email.trim(),
      loginData.password
    );

    const data = response.data;

    if (!data?.token && data?.role === undefined) {
      throw new Error('Invalid login response');
    }

    // Convert numeric role to string (0 = Customer, 1 = Admin)
    const roleMap = { 0: 'Customer', 1: 'Admin' };
    const userRole = typeof data.role === 'number' ? roleMap[data.role] : data.role;

    // ðŸ” Role mismatch protection
    if (userRole !== loginRole) {
      throw new Error(
        `This account is registered as ${userRole}. Please select ${userRole} login.`
      );
    }

    // Persist session
    sessionStorage.setItem('token', data.token);
    sessionStorage.setItem('user', JSON.stringify({ ...data, role: userRole }));

    setUser({
      email: data.email,
      role: userRole,
      name: data.fullName,
      mobile: data.mobile || ''
    });

    if (userRole === 'Admin') {
      setActiveTab('overview');
      setCurrentPage('admin');
    } else {
      setCurrentPage('products');
    }

  } catch (err) {
    setError(
      err.response?.data?.message ||
      err.message ||
      'Login failed'
    );
  } finally {
    setLoading(false);
  }
};


const handleRegister = async () => {
  const { fullName, email, mobile, password, confirmPassword } = registerData;

  // Validation
  if (!fullName?.trim()) {
    setError('Full name is required');
    return;
  }

  if (!email?.trim()) {
    setError('Email is required');
    return;
  }

  if (!mobile?.trim()) {
    setError('Mobile number is required');
    return;
  }

  if (!password) {
    setError('Password is required');
    return;
  }

  if (password !== confirmPassword) {
    setError('Passwords do not match');
    return;
  }

  if (password.length < 6) {
    setError('Password must be at least 6 characters');
    return;
  }

  setLoading(true);
  setError('');

  try {
    console.log('Attempting registration:', { fullName, email, mobile, role: loginRole });

    const response = await authAPI.register(
      fullName.trim(),
      email.trim(),
      mobile.trim(),
      password,
      loginRole
    );

    // Verify the backend returned a valid role
    if (!response.data.role) {
      throw new Error('No role returned from server');
    }


    console.log('Registration response:', response.data);

    const data = response.data;

    // Validate response
    if (!data.token) {
      throw new Error('No token received from server');
    }

    // Store token and user data
    sessionStorage.setItem('token', data.token);
    sessionStorage.setItem('user', JSON.stringify(data));

    // Set user state
    setUser({
      email: data.email,
      role: data.role,
      name: data.fullName,
      mobile: data.mobile || mobile
    });

    // Set profile data
    setProfileData({
      fullName: data.fullName || fullName,
      email: data.email || email,
      mobile: data.mobile || mobile,
      addresses: []
    });

    console.log('Registration successful');

    // Redirect to products (new users are always customers)
    setCurrentPage('products');

  } catch (err) {
    console.error('Registration error:', err);
    console.error('Error response:', err.response?.data);

    const errorMessage = err.response?.data?.message || 
                        err.response?.data?.title ||
                        err.response?.data?.errors?.Email?.[0] ||
                        err.message || 
                        'Registration failed. Please try again.';

    setError(errorMessage);
  } finally {
    setLoading(false);
  }
};                           

const handleLogout = () => {
  // Clear storage
  sessionStorage.removeItem('token');
  sessionStorage.removeItem('user');

  // Reset all state
  setUser(null);
  setCurrentPage('home');
  setCart([]);
  setOrders([]);
  setChatMessages([]);
  setChatOpen(false);
  setProfileData({
    fullName: '',
    email: '',
    mobile: '',
    addresses: []
  });

  console.log('User logged out successfully');
};

// Add this where you fetch customers
useEffect(() => {
  const fetchCustomers = async () => {
    try {
      const response = await adminAPI.getUsers();

      console.log('Admin users response:', response.data);

      // Axios always puts payload in response.data
      const users = Array.isArray(response.data)
        ? response.data
        : response.data?.users || [];

      setAdminCustomers(users);
      setCustomers(users);

    } catch (error) {
      console.error('Error fetching customers:', error);
      setAdminCustomers([]);
      setCustomers([]);
    }
  };

  if (activeTab === 'customers' && user?.role === 'Admin') {
    fetchCustomers();
  }
}, [activeTab, user]);

// At the top of your component, after state declarations
useEffect(() => {
  const token = sessionStorage.getItem('token');
  const savedUser = sessionStorage.getItem('user');

  if (!token || !savedUser) return;

  try {
    const userData = JSON.parse(savedUser);

    if (!userData.role || !userData.email) {
      throw new Error('Invalid session');
    }

    setUser({
      email: userData.email,
      role: userData.role,
      name: userData.fullName || userData.name,
      mobile: userData.mobile || ''
    });

    if (userData.role === 'Admin') {
      setActiveTab('overview');
      setCurrentPage('admin');
    } else {
      setCurrentPage('products');
      loadProducts();
    }

  } catch {
    sessionStorage.clear();
    setUser(null);
    setCurrentPage('home');
  }
}, []);

const fetchCart = async () => {
  if (!user) {
    // Guest user - load from localStorage
    const storedCart = localStorage.getItem('cart');
    if (storedCart) {
      try {
        setCart(JSON.parse(storedCart));
      } catch (e) {
        console.error('Error parsing cart from localStorage:', e);
        setCart([]);
      }
    }
    return;
  }

  // Logged-in user - fetch from backend
  try {
    const response = await cartAPI.view();
    const backendCart = response.data?.items || response.data || [];
    setCart(backendCart);
    // Keep localStorage in sync for offline access
    localStorage.setItem('cart', JSON.stringify(backendCart));
  } catch (err) {
    console.error('Error fetching cart:', err);
    // Fallback to localStorage if backend fails
    const storedCart = localStorage.getItem('cart');
    if (storedCart) {
      try {
        setCart(JSON.parse(storedCart));
      } catch (e) {
        setCart([]);
      }
    }
  }
};

