// âœ… FIXED: Add to cart with proper backend sync
const addToCart = async (product) => {
  if (user) {
    // Logged-in user â†’ backend cart
    try {
      const response = await cartAPI.add({ 
        productId: product.id, 
        quantity: 1 
      });

      const updatedCart = response.data?.items || response.data || [];
      setCart(updatedCart);
      localStorage.setItem('cart', JSON.stringify(updatedCart));

    } catch (err) {
      console.error('Error adding to cart:', err);
      setError(err.response?.data?.message || 'Failed to add item to cart.');
    }
  } else {
    // Guest user â†’ localStorage cart
    const updatedCart = [...cart];
    const existing = updatedCart.find(item => item.productId === product.id);

    if (existing) {
      existing.quantity += 1;
    } else {
      updatedCart.push({
        productId: product.id,
        productName: product.name,
        price: product.price,
        quantity: 1,
        imageUrl: product.image
      });
    }

    setCart(updatedCart);
    localStorage.setItem('cart', JSON.stringify(updatedCart));
    setError('Login to save your cart permanently.');
  }
};

// âœ… FIXED: Update cart quantity with backend sync
const updateCartQuantity = async (productId, quantity) => {
  if (quantity <= 0) {
    await removeFromCart(productId);
    return;
  }

  if (user) {
    // Logged-in user â†’ update backend
    try {
      await cartAPI.update(productId, quantity);

      // Update local state
      setCart(prev => {
        const updatedCart = prev.map(item =>
          item.productId === productId
            ? { ...item, quantity }
            : item
        );
        localStorage.setItem('cart', JSON.stringify(updatedCart));
        return updatedCart;
      });

    } catch (err) {
      console.error('Error updating cart:', err);
      setError(err.response?.data?.message || 'Failed to update cart');
    }
  } else {
    // Guest user â†’ update localStorage
    setCart(prev => {
      const updatedCart = prev.map(item =>
        item.productId === productId
          ? { ...item, quantity }
          : item
      );
      localStorage.setItem('cart', JSON.stringify(updatedCart));
      return updatedCart;
    });
  }
};

// âœ… FIXED: Remove from cart with backend sync
const removeFromCart = async (productId) => {
  if (user) {
    // Logged-in user â†’ remove from backend
    try {
      await cartAPI.remove(productId);

      setCart(prev => {
        const updatedCart = prev.filter(item => item.productId !== productId);
        localStorage.setItem('cart', JSON.stringify(updatedCart));
        return updatedCart;
      });

    } catch (err) {
      console.error('Error removing from cart:', err);
      setError(err.response?.data?.message || 'Failed to remove item from cart');
    }
  } else {
    // Guest user â†’ remove from localStorage
    setCart(prev => {
      const updatedCart = prev.filter(item => item.productId !== productId);
      localStorage.setItem('cart', JSON.stringify(updatedCart));
      return updatedCart;
    });
  }
};
// âœ… OPTIONAL: Merge guest cart with backend cart on login
const mergeGuestCartOnLogin = async () => {
  const guestCart = localStorage.getItem('cart');
  if (!guestCart || !user) return;

  try {
    const guestItems = JSON.parse(guestCart);

    // Add each guest cart item to backend
    for (const item of guestItems) {
      try {
        await cartAPI.add({
          productId: item.productId,
          quantity: item.quantity
        });
      } catch (err) {
        console.error('Error merging cart item:', item, err);
      }
    }

    // Fetch updated cart from backend
    await fetchCart();

  } catch (err) {
    console.error('Error merging guest cart:', err);
  }
};


// Load Razorpay script
const loadRazorpayScript = () => {
  return new Promise((resolve) => {
    if (window.Razorpay) return resolve(true);

    const script = document.createElement('script');
    script.src = 'https://checkout.razorpay.com/v1/checkout.js';
    script.onload = () => resolve(true);
    script.onerror = () => resolve(false);
    document.body.appendChild(script);
  });
};

