                      for (const field of fields) {
                      let value;
                      if (field.type === 'textarea') {
                        value = window.prompt(`${field.label}:`, '');
                      } else if (field.type === 'number') {
                        value = window.prompt(`${field.label}:`, '0');
                      } else {
