import React, { useState, useEffect, useRef } from 'react';
import { Mail, Lock, User, Phone, Eye, EyeOff, ShoppingBag, TrendingUp, Package, Users, BarChart3, Settings, MessageCircle, Search, X, Send, ShoppingCart, LogOut, UserCircle, MapPin, CreditCard, Clock } from 'lucide-react';

const CompleteIntegratedApp = () => {
  const [currentPage, setCurrentPage] = useState('home');
  const [user, setUser] = useState(null);
  const [showPassword, setShowPassword] = useState(false);
  const [loginData, setLoginData] = useState({ email: '', password: '' });
  const [registerData, setRegisterData] = useState({ fullName: '', email: '', mobile: '', password: '', confirmPassword: '' });
  const [error, setError] = useState('');
  const [activeTab, setActiveTab] = useState('overview');

  // Shopping state
  const [products, setProducts] = useState([]);
  const [cart, setCart] = useState([]);
  const [chatOpen, setChatOpen] = useState(false);
  const [chatMessages, setChatMessages] = useState([]);
  const [currentMessage, setCurrentMessage] = useState('');
  const [isTyping, setIsTyping] = useState(false);
  const chatEndRef = useRef(null);

  // Profile state
  const [editMode, setEditMode] = useState(false);
  const [profileData, setProfileData] = useState({
    fullName: '',
    email: '',
    mobile: '',
    addresses: []
  });

  // Initialize products
  useEffect(() => {
    const sampleProducts = [
      {
        id: 1,
        name: 'Premium Smartphone X1',
        brand: 'TechBrand',
        price: 9999,
        rating: 4.5,
        reviewCount: 1250,
        image: 'https://via.placeholder.com/300x300/3b82f6/ffffff?text=Smartphone',
        category: 'Electronics',
        description: 'Powerful processor, stunning camera, all-day battery',
        specs: '6GB RAM, 128GB Storage, 48MP Camera'
      },
      {
        id: 2,
        name: 'Wireless Earbuds Pro',
        brand: 'AudioMax',
        price: 2999,
        rating: 4.7,
        reviewCount: 890,
        image: 'https://via.placeholder.com/300x300/8b5cf6/ffffff?text=Earbuds',
        category: 'Audio',
        description: 'Active noise cancellation, 30hr battery life',
        specs: 'Bluetooth 5.2, IPX4 Water Resistant'
      },
      {
        id: 3,
        name: 'Smart Watch Ultra',
        brand: 'FitTech',
        price: 4999,
        rating: 4.3,
        reviewCount: 567,
        image: 'https://via.placeholder.com/300x300/10b981/ffffff?text=Watch',
        category: 'Wearables',
        description: 'Health tracking, GPS, always-on display',
        specs: 'Heart Rate Monitor, Sleep Tracking, 7-day Battery'
      },
      {
        id: 4,
        name: 'Gaming Laptop Z9',
        brand: 'GameForce',
        price: 59999,
        rating: 4.8,
        reviewCount: 432,
        image: 'https://via.placeholder.com/300x300/ef4444/ffffff?text=Laptop',
        category: 'Computers',
        description: 'High-performance gaming, RGB keyboard',
        specs: 'RTX 3060, 16GB RAM, 512GB SSD, 144Hz Display'
      },
      {
        id: 5,
        name: 'Camera DSLR Pro',
        brand: 'PhotoMax',
        price: 45999,
        rating: 4.6,
        reviewCount: 289,
        image: 'https://via.placeholder.com/300x300/f59e0b/ffffff?text=Camera',
        category: 'Photography',
        description: 'Professional photography, 4K video',
        specs: '24MP Sensor, Dual Card Slots, Weather Sealed'
      },
      {
        id: 6,
        name: 'Tablet Pro 11',
        brand: 'TechBrand',
        price: 34999,
        rating: 4.4,
        reviewCount: 678,
        image: 'https://via.placeholder.com/300x300/06b6d4/ffffff?text=Tablet',
        category: 'Electronics',
        description: 'Stunning display, all-day battery, perfect for work',
        specs: '11-inch Display, 128GB Storage, Stylus Support'
      }
    ];
    setProducts(sampleProducts);
  }, []);

  // Update profile when user logs in
  useEffect(() => {
    if (user) {
      setProfileData({
        fullName: user.name,
        email: user.email,
        mobile: user.mobile || '+91 98765 43210',
        addresses: [
          {
            id: 1,
            type: 'Home',
            line1: '123 Main Street',
            line2: 'Apartment 4B',
            city: 'Chennai',
            state: 'Tamil Nadu',
            postal: '600001',
            isDefault: true
          }
        ]
      });
    }
  }, [user]);

  const handleLogin = () => {
    setError('');
    if (loginData.email === 'admin@ecommerce.com' && loginData.password === 'admin123') {
      setUser({ email: loginData.email, role: 'Admin', name: 'Admin User', mobile: '+91 99999 99999' });
      setCurrentPage('admin');
    } else if (loginData.email === 'customer@test.com' && loginData.password === 'customer123') {
      setUser({ email: loginData.email, role: 'Customer', name: 'John Doe', mobile: '+91 98765 43210' });
      setCurrentPage('products'); // Redirect to products page
    } else {
      setError('Invalid credentials. Try: admin@ecommerce.com / admin123 or customer@test.com / customer123');
    }
  };

  const handleRegister = () => {
    setError('');
    if (registerData.password !== registerData.confirmPassword) {
      setError('Passwords do not match');
      return;
    }
    if (registerData.password.length < 6) {
      setError('Password must be at least 6 characters');
      return;
    }
    setUser({ 
      email: registerData.email, 
      role: 'Customer', 
      name: registerData.fullName,
      mobile: registerData.mobile
    });
    setCurrentPage('products'); // Redirect to products page
  };

  const handleLogout = () => {
    setUser(null);
    setCurrentPage('home');
    setCart([]);
    setChatMessages([]);
    setChatOpen(false);
  };

  // AI Chat simulation
  const sendMessageToAgent = async (message) => {
    setIsTyping(true);

    const userMsg = { role: 'user', content: message };
    setChatMessages(prev => [...prev, userMsg]);
    setCurrentMessage('');

    setTimeout(() => {
      let agentResponse = '';
      const lowerMsg = message.toLowerCase();

      if (lowerMsg.includes('hi') || lowerMsg.includes('hello')) {
        agentResponse = "Hello! I'm your AI shopping assistant. I can help you find products, add items to cart, or answer questions about your orders. What are you looking for today?";
      } else if (lowerMsg.includes('smartphone') || lowerMsg.includes('phone')) {
        const phones = products.filter(p => p.category === 'Electronics');
        if (lowerMsg.includes('under') || lowerMsg.includes('below')) {
          agentResponse = "I found some great smartphones under â‚¹10,000! Here's the best option:\n\nðŸ“± Premium Smartphone X1 by TechBrand\nPrice: â‚¹9,999\nRating: 4.5/5 (1,250 reviews)\nFeatures: 6GB RAM, 128GB Storage, 48MP Camera\n\nWould you like me to add this to your cart?";
        } else {
          agentResponse = `I found ${phones.length} smartphones for you! The Premium Smartphone X1 is our top pick with excellent camera and battery life. Would you like more details?`;
        }
      } else if (lowerMsg.includes('add') && lowerMsg.includes('cart')) {
        const newItem = products[0];
        setCart(prev => {
          const existing = prev.find(item => item.id === newItem.id);
          if (existing) {
            return prev.map(item => item.id === newItem.id ? {...item, quantity: item.quantity + 1} : item);
          }
          return [...prev, { ...newItem, quantity: 1 }];
        });
        agentResponse = `Great choice! I've added ${newItem.name} to your cart. Your cart now has ${cart.length + 1} items. Would you like to continue shopping or proceed to checkout?`;
      } else if (lowerMsg.includes('cart') || lowerMsg.includes('show')) {
        if (cart.length === 0) {
          agentResponse = "Your cart is currently empty. Would you like me to help you find some products?";
        } else {
          const total = cart.reduce((sum, item) => sum + item.price * item.quantity, 0);
          agentResponse = `You have ${cart.length} items in your cart:\n\n${cart.map(item => `â€¢ ${item.name} - â‚¹${item.price} x ${item.quantity}`).join('\n')}\n\nTotal: â‚¹${total}\n\nReady to checkout?`;
        }
      } else if (lowerMsg.includes('recommend') || lowerMsg.includes('suggest')) {
        agentResponse = "Based on popular choices, I recommend:\n\n1. Premium Smartphone X1 - Best value for money\n2. Wireless Earbuds Pro - Great audio quality\n3. Smart Watch Ultra - Perfect for fitness tracking\n\nWhich category interests you most?";
      } else if (lowerMsg.includes('checkout') || lowerMsg.includes('buy')) {
        agentResponse = "Perfect! To proceed with checkout, I'll need to confirm your shipping address. You have 1 saved address. Would you like to use it, or add a new one?";
      } else {
        agentResponse = "I can help you with:\nâ€¢ Finding products by category or budget\nâ€¢ Adding items to your cart\nâ€¢ Checking order status\nâ€¢ Product recommendations\n\nWhat would you like to do?";
      }

      const assistantMsg = { role: 'assistant', content: agentResponse };
      setChatMessages(prev => [...prev, assistantMsg]);
      setIsTyping(false);
    }, 1000 + Math.random() * 1000);
  };

  const addToCart = (product) => {
    const existing = cart.find(item => item.id === product.id);
    if (existing) {
      setCart(cart.map(item => 
        item.id === product.id 
          ? { ...item, quantity: item.quantity + 1 }
          : item
      ));
    } else {
      setCart([...cart, { ...product, quantity: 1 }]);
    }
  };

  useEffect(() => {
    chatEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [chatMessages]);

  const stats = [
    { label: 'Total Sales', value: 'â‚¹2,45,890', change: '+12.5%', icon: <TrendingUp className="w-6 h-6" /> },
    { label: 'Total Orders', value: '1,234', change: '+8.2%', icon: <Package className="w-6 h-6" /> },
    { label: 'Total Customers', value: '856', change: '+15.3%', icon: <Users className="w-6 h-6" /> },
    { label: 'Total Products', value: '142', change: '+3', icon: <ShoppingBag className="w-6 h-6" /> }
  ];

  // Landing Page
  if (!user && currentPage === 'home') {
    return (
      <div className="min-h-screen bg-gradient-to-br from-blue-50 via-purple-50 to-pink-50">
        <div className="max-w-7xl mx-auto px-4 py-12">
          <div className="text-center mb-16">
            <div className="inline-flex items-center gap-3 mb-6">
              <ShoppingBag className="w-16 h-16 text-blue-600" />
              <h1 className="text-5xl font-bold text-gray-900">ShopAI</h1>
            </div>
            <p className="text-2xl text-gray-600 mb-8">AI-Powered E-Commerce Platform</p>
            <p className="text-lg text-gray-500 max-w-2xl mx-auto mb-12">
              Experience the future of online shopping with our intelligent AI assistant
            </p>
            <div className="flex gap-4 justify-center">
              <button onClick={() => setCurrentPage('login')} className="px-8 py-4 bg-blue-600 text-white rounded-xl font-semibold hover:bg-blue-700 transition-colors shadow-lg">
                Sign In
              </button>
              <button onClick={() => setCurrentPage('register')} className="px-8 py-4 bg-white text-blue-600 rounded-xl font-semibold hover:bg-gray-50 transition-colors shadow-lg border-2 border-blue-600">
                Create Account
              </button>
            </div>
          </div>

          <div className="grid md:grid-cols-3 gap-8 mt-16">
            <div className="bg-white rounded-2xl p-8 shadow-lg">
              <div className="w-12 h-12 bg-blue-100 rounded-xl flex items-center justify-center mb-4">
                <Package className="w-6 h-6 text-blue-600" />
              </div>
              <h3 className="text-xl font-bold text-gray-900 mb-2">Smart Shopping</h3>
              <p className="text-gray-600">AI-powered product recommendations</p>
            </div>
            <div className="bg-white rounded-2xl p-8 shadow-lg">
              <div className="w-12 h-12 bg-purple-100 rounded-xl flex items-center justify-center mb-4">
                <TrendingUp className="w-6 h-6 text-purple-600" />
              </div>
              <h3 className="text-xl font-bold text-gray-900 mb-2">Best Prices</h3>
              <p className="text-gray-600">Intelligent price comparison</p>
            </div>
            <div className="bg-white rounded-2xl p-8 shadow-lg">
              <div className="w-12 h-12 bg-pink-100 rounded-xl flex items-center justify-center mb-4">
                <Settings className="w-6 h-6 text-pink-600" />
              </div>
              <h3 className="text-xl font-bold text-gray-900 mb-2">Easy Management</h3>
              <p className="text-gray-600">Seamless cart and checkout</p>
            </div>
          </div>
        </div>
      </div>
    );
  }

  // Login Page
  if (!user && currentPage === 'login') {
    return (
      <div className="min-h-screen bg-gradient-to-br from-blue-50 to-purple-50 flex items-center justify-center p-4">
        <div className="max-w-md w-full">
          <div className="text-center mb-8">
            <div className="inline-flex items-center gap-2 mb-2">
              <ShoppingBag className="w-10 h-10 text-blue-600" />
              <h1 className="text-3xl font-bold text-gray-900">ShopAI</h1>
            </div>
            <p className="text-gray-600">AI-Powered Shopping Platform</p>
          </div>

          <div className="bg-white rounded-2xl shadow-xl p-8">
            <h2 className="text-2xl font-bold text-gray-900 mb-2">Welcome Back</h2>
            <p className="text-gray-600 mb-6">Sign in to continue shopping</p>

            {error && (
              <div className="bg-red-50 border border-red-200 text-red-700 px-4 py-3 rounded-lg mb-4 text-sm">
                {error}
              </div>
            )}

            <div className="space-y-4">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">Email Address</label>
                <div className="relative">
                  <Mail className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 w-5 h-5" />
                  <input
                    type="email"
                    value={loginData.email}
                    onChange={(e) => setLoginData({ ...loginData, email: e.target.value })}
                    onKeyPress={(e) => e.key === 'Enter' && handleLogin()}
                    className="w-full pl-10 pr-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                    placeholder="you@example.com"
                  />
                </div>
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">Password</label>
                <div className="relative">
                  <Lock className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 w-5 h-5" />
                  <input
                    type={showPassword ? 'text' : 'password'}
                    value={loginData.password}
                    onChange={(e) => setLoginData({ ...loginData, password: e.target.value })}
                    onKeyPress={(e) => e.key === 'Enter' && handleLogin()}
                    className="w-full pl-10 pr-12 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                    placeholder="â€¢â€¢â€¢â€¢â€¢â€¢â€¢â€¢"
                  />
                  <button
                    onClick={() => setShowPassword(!showPassword)}
                    className="absolute right-3 top-1/2 transform -translate-y-1/2 text-gray-400 hover:text-gray-600"
                  >
                    {showPassword ? <EyeOff className="w-5 h-5" /> : <Eye className="w-5 h-5" />}
                  </button>
                </div>
              </div>

              <button onClick={handleLogin} className="w-full bg-blue-600 text-white py-3 rounded-lg font-semibold hover:bg-blue-700 transition-colors">
                Sign In
              </button>
            </div>

            <div className="mt-6 text-center">
              <p className="text-gray-600">
                Don't have an account?{' '}
                <button onClick={() => setCurrentPage('register')} className="text-blue-600 hover:text-blue-700 font-semibold">
                  Sign Up
                </button>
              </p>
            </div>

            <div className="mt-6 p-4 bg-blue-50 rounded-lg">
              <p className="text-sm font-semibold text-blue-900 mb-2">Demo Credentials:</p>
              <div className="text-xs text-blue-800 space-y-1">
                <p>ðŸ‘¤ Customer: customer@test.com / customer123</p>
                <p>ðŸ” Admin: admin@ecommerce.com / admin123</p>
              </div>
            </div>
          </div>

          <div className="text-center mt-6">
            <button onClick={() => setCurrentPage('home')} className="text-gray-600 hover:text-gray-900">
              â† Back to Home
            </button>
          </div>
        </div>
      </div>
    );
  }

  // Register Page
  if (!user && currentPage === 'register') {
    return (
      <div className="min-h-screen bg-gradient-to-br from-blue-50 to-purple-50 flex items-center justify-center p-4">
        <div className="max-w-md w-full">
          <div className="text-center mb-8">
            <div className="inline-flex items-center gap-2 mb-2">
              <ShoppingBag className="w-10 h-10 text-blue-600" />
              <h1 className="text-3xl font-bold text-gray-900">ShopAI</h1>
            </div>
            <p className="text-gray-600">Create your account</p>
          </div>

          <div className="bg-white rounded-2xl shadow-xl p-8">
            <h2 className="text-2xl font-bold text-gray-900 mb-2">Sign Up</h2>
            <p className="text-gray-600 mb-6">Join thousands of happy shoppers</p>

            {error && (
              <div className="bg-red-50 border border-red-200 text-red-700 px-4 py-3 rounded-lg mb-4 text-sm">
                {error}
              </div>
            )}

            <div className="space-y-4">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">Full Name</label>
                <div className="relative">
                  <User className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 w-5 h-5" />
                  <input
                    type="text"
                    value={registerData.fullName}
                    onChange={(e) => setRegisterData({ ...registerData, fullName: e.target.value })}
                    className="w-full pl-10 pr-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500"
                    placeholder="John Doe"
                  />
                </div>
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">Email</label>
                <div className="relative">
                  <Mail className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 w-5 h-5" />
                  <input
                    type="email"
                    value={registerData.email}
                    onChange={(e) => setRegisterData({ ...registerData, email: e.target.value })}
                    className="w-full pl-10 pr-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500"
                    placeholder="you@example.com"
                  />
                </div>
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">Mobile</label>
                <div className="relative">
                  <Phone className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 w-5 h-5" />
                  <input
                    type="tel"
                    value={registerData.mobile}
                    onChange={(e) => setRegisterData({ ...registerData, mobile: e.target.value })}
                    className="w-full pl-10 pr-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500"
                    placeholder="+91 98765 43210"
                  />
                </div>
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">Password</label>
                <div className="relative">
                  <Lock className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 w-5 h-5" />
                  <input
                    type={showPassword ? 'text' : 'password'}
                    value={registerData.password}
                    onChange={(e) => setRegisterData({ ...registerData, password: e.target.value })}
                    className="w-full pl-10 pr-12 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500"
                    placeholder="â€¢â€¢â€¢â€¢â€¢â€¢â€¢â€¢"
                  />
                  <button onClick={() => setShowPassword(!showPassword)} className="absolute right-3 top-1/2 transform -translate-y-1/2">
                    {showPassword ? <EyeOff className="w-5 h-5" /> : <Eye className="w-5 h-5" />}
                  </button>
                </div>
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">Confirm Password</label>
                <div className="relative">
                  <Lock className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 w-5 h-5" />
                  <input
                    type={showPassword ? 'text' : 'password'}
                    value={registerData.confirmPassword}
                    onChange={(e) => setRegisterData({ ...registerData, confirmPassword: e.target.value })}
                    onKeyPress={(e) => e.key === 'Enter' && handleRegister()}
                    className="w-full pl-10 pr-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500"
                    placeholder="â€¢â€¢â€¢â€¢â€¢â€¢â€¢â€¢"
                  />
                </div>
              </div>

              <button onClick={handleRegister} className="w-full bg-blue-600 text-white py-3 rounded-lg font-semibold hover:bg-blue-700">
                Create Account
              </button>
            </div>

            <div className="mt-6 text-center">
              <p className="text-gray-600">
                Already have an account?{' '}
                <button onClick={() => setCurrentPage('login')} className="text-blue-600 hover:text-blue-700 font-semibold">
                  Sign In
                </button>
              </p>
            </div>
          </div>

          <div className="text-center mt-6">
            <button onClick={() => setCurrentPage('home')} className="text-gray-600 hover:text-gray-900">
              â† Back to Home
            </button>
          </div>
        </div>
      </div>
    );
  }

  // Profile Page
  if (user && currentPage === 'profile') {
    return (
      <div className="min-h-screen bg-gray-50">
        {/* Header */}
        <header className="bg-white shadow-sm sticky top-0 z-40">
          <div className="max-w-7xl mx-auto px-4 py-4">
            <div className="flex items-center justify-between">
              <div className="flex items-center gap-2">
                <ShoppingBag className="w-8 h-8 text-blue-600" />
                <h1 className="text-2xl font-bold text-gray-900">ShopAI</h1>
              </div>

              <div className="flex items-center gap-4">
                <button onClick={() => setCurrentPage('products')} className="text-gray-600 hover:text-gray-900">
                  Products
                </button>
                <button onClick={() => setCurrentPage('cart')} className="relative p-2 hover:bg-gray-100 rounded-lg">
