import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import { Mail, Lock, User, Phone, Eye, EyeOff, ShoppingBag, TrendingUp, Package, Users, BarChart3, Settings, MessageCircle, Search, X, Send, ShoppingCart, LogOut, UserCircle, MapPin, CreditCard, Clock, Plus, Minus, Trash2 } from 'lucide-react';
import {
  authAPI,
  productsAPI,
  cartAPI,
  ordersAPI,
  recommendationsAPI
} from './api'; // adjust path if needed

// Add response interceptor to handle 401 errors globally
const setupInterceptors = () => {
  const apis = [authAPI, productsAPI, cartAPI, ordersAPI, recommendationsAPI];

  apis.forEach(api => {
    if (api && api.interceptors) {
      // Response interceptor for 401 handling
      api.interceptors.response.use(
        response => response,
        error => {
          if (error.response?.status === 401 && sessionStorage.getItem('token')) {
            sessionStorage.removeItem('token');
            sessionStorage.removeItem('user');
            window.location.href = '/';
          }
          return Promise.reject(error);
        }
      );

      // Request interceptor to add token
      api.interceptors.request.use(
        config => {
          const token = sessionStorage.getItem('token');
          if (token) {
            config.headers.Authorization = `Bearer ${token}`;
          }
          return config;
        },
        error => Promise.reject(error)
      );
    }
  });
};

setupInterceptors();

// Ensure authAPI.register accepts role parameter
const originalRegister = authAPI.register;
authAPI.register = (fullName, email, mobile, password, role) =>
  originalRegister(fullName, email, mobile, password, role);

