import axios from 'axios';

const API_BASE_URL = 'http://localhost:5033/api';

// Create axios instance
const api = axios.create({
  baseURL: API_BASE_URL,
  headers: {
    'Content-Type': 'application/json',
  },
});

// Backwards-compatible patches so the frontend can:
// - attach interceptors to each exported API object (frontend expects `api.interceptors` on each)
// - support authAPI.register(..., role)
// - provide authAPI.getAll() for admin/customer listing if missing
// This runs after the module finishes evaluating (APIs are defined later).
setTimeout(() => {
  const safeAttachInterceptors = (obj) => {
    if (obj && !obj.interceptors) {
      try {
        Object.defineProperty(obj, 'interceptors', {
          value: api.interceptors,
          writable: false,
          enumerable: false,
        });
      } catch (e) {
        // ignore
      }
    }
  };

  // authAPI
  if (typeof authAPI !== 'undefined') {
    safeAttachInterceptors(authAPI);

    // ensure register accepts role (send role if provided)
    const originalRegister = authAPI.register;
    authAPI.register = (fullName, email, mobile, password, role) => {
      // prefer calling backend with role in payload
      return api.post('/auth/register', { fullName, email, mobile, password, role });
    };

    // provide a sensible getAll fallback for admin listing if backend exposes /admin/users
    if (typeof authAPI.getAll !== 'function') {
      authAPI.getAll = () => api.get('/admin/users');
    }
  }

  // other APIs
  if (typeof productsAPI !== 'undefined') safeAttachInterceptors(productsAPI);
  if (typeof cartAPI !== 'undefined') safeAttachInterceptors(cartAPI);
  if (typeof ordersAPI !== 'undefined') safeAttachInterceptors(ordersAPI);
  if (typeof recommendationsAPI !== 'undefined') safeAttachInterceptors(recommendationsAPI);
  if (typeof adminAPI !== 'undefined') safeAttachInterceptors(adminAPI);
}, 0);

// ✅ Always attach JWT token (USE sessionStorage)
api.interceptors.request.use(
  (config) => {
    const token = sessionStorage.getItem('token');
    if (token) {
      config.headers.Authorization = `Bearer ${token}`;
    }
    return config;
  },
  (error) => Promise.reject(error)
);

/* ===================== AUTH API ===================== */

export const authAPI = {
  login: (email, password) =>
    api.post('/auth/login', { email, password }),

  register: (fullName, email, mobile, password) =>
    api.post('/auth/register', { fullName, email, mobile, password }),

  getProfile: () =>
    api.get('/user/profile'),
};

/* ===================== PRODUCTS API ===================== */

export const productsAPI = {
  getAll: () =>
    api.get('/products'),

  search: (query, categoryId, minPrice, maxPrice) =>
    api.get('/products/search', {
      params: { query, categoryId, minPrice, maxPrice }
    }),

  getById: (id) =>
    api.get(`/products/${id}`),
};

/* ===================== CART API (FIXED) ===================== */

export const cartAPI = {
  // View cart
  view: () =>
    api.get('/cart/view'),

  // ✅ FIXED: accept DTO object
  add: (data) =>
    api.post('/cart/add', data),

  // Remove item
  remove: (productId) =>
    api.delete(`/cart/remove/${productId}`),

  // Update quantity
  update: (productId, quantity) =>
    api.put('/cart/update', { productId, quantity }),

  // Clear cart (optional but useful)
  clear: () =>
    api.delete('/cart/clear'),
};

/* ===================== ORDERS API ===================== */

export const ordersAPI = {
  confirm: (shippingAddressId) =>
    api.post('/order/confirm', { shippingAddressId }),

  history: () =>
    api.get('/order/history'),

  getById: (id) =>
    api.get(`/order/${id}`),
};

/* ===================== AI RECOMMENDATIONS API ===================== */

export const recommendationsAPI = {
  rank: (productIds, criteria) =>
    api.post('/recommendations/rank', { productIds, criteria }),
};

/* ===================== PAYMENT API ===================== */

export const paymentAPI = {
  initiate: (orderId, amount, paymentMethod) =>
    api.post('/payment/initiate', { orderId, amount, paymentMethod }),

  verify: (paymentId, transactionId) =>
    api.post('/payment/verify', { paymentId, transactionId }),

  getStatus: (paymentId) =>
    api.get(`/payment/status/${paymentId}`),
};

/* ===================== INVENTORY API ===================== */

export const inventoryAPI = {
  getStock: (productId) =>
    api.get(`/inventory/stock/${productId}`),

  checkAvailability: (productId, quantity) =>
    api.get('/inventory/check', {
      params: { productId, quantity }
    }),
};

/* ===================== ADMIN API ===================== */

export const adminAPI = {
  getDashboard: () =>
    api.get('/admin/dashboard'),

  getUsers: () =>
    api.get('/admin/users'),

  getOrders: () =>
    api.get('/admin/orders'),

  createProduct: (data) =>
    api.post('/admin/products', data),

  updateProduct: (id, data) =>
    api.put(`/admin/products/${id}`, data),

  deleteProduct: (id) =>
    api.delete(`/admin/products/${id}`),
};
// Remove duplicate export statement
export default api;
