// Load profile from backend (map backend Address entity to frontend shape; fallback to localStorage)
const loadProfile = async () => {
  if (!user) return;
  try {
    const response = await authAPI.getProfile();
    const data = response.data || {};

    // Backend may return addresses in different shapes. Normalize to a frontend-friendly shape.
    const rawAddresses = data.addresses || data.addressList || [];
    let addresses = Array.isArray(rawAddresses)
      ? rawAddresses.map(a => {
          // Build a human-readable address string if not provided
          const addrLine1 = a.AddressLine1 || a.address || a.line1 || '';
          const addrLine2 = a.AddressLine2 || a.line2 || '';
          const city = a.City || a.city || '';
          const state = a.State || a.state || '';
          const postal = a.PostalCode || a.postalCode || a.pincode || '';
          const country = a.Country || a.country || '';

          const addressString = a.address || [
            addrLine1,
            addrLine2,
            city,
            state,
            postal,
            country
          ].filter(Boolean).join(', ').trim();

          return {
            id: a.Id ?? a.id ?? a.addressId ?? null,
            label: a.label || (a.IsDefault ? 'Home' : a.type) || 'Address',
            AddressLine1: addrLine1 || '',
            AddressLine2: addrLine2 || '',
            City: city || '',
            State: state || '',
            PostalCode: postal || '',
            Country: country || '',
            IsDefault: !!a.IsDefault,
            address: addressString || ''
          };
        })
      : [];

    // If backend returned no addresses, try a localStorage fallback (survives logout)
    if ((!addresses || addresses.length === 0) && typeof localStorage !== 'undefined') {
      try {
        const saved = JSON.parse(localStorage.getItem('savedAddresses') || 'null');
        if (Array.isArray(saved) && saved.length > 0) {
          addresses = saved;
        }
      } catch (e) {
        // ignore parse errors
      }
    }

    setProfileData({
      fullName: data.fullName || '',
      email: data.email || '',
      mobile: (data.mobile || data.Mobile || '').toString().trim(),
      addresses: addresses || []
    });
  } catch (err) {
    console.error('Error loading profile:', err);

    // fallback to local storage only (in case backend fails)
    try {
      const saved = JSON.parse(localStorage.getItem('savedAddresses') || 'null') || [];
      setProfileData(prev => ({
        ...prev,
        addresses: saved
      }));
    } catch {
      // noop
    }
  }
};

