const addToCart = async (product) => {
  const updatedCart = [...cart]; // local copy

  if (user) {
    // Logged-in â†’ backend cart
    try {
      const response = await cartAPI.add({ productId: product.id, quantity: 1 });
      setCart(response.data.items ?? []);
      localStorage.setItem('cart', JSON.stringify(response.data.items ?? []));
    } catch (err) {
      console.error(err);
      setError('Failed to add item to cart.');
    }
  } else {
    // Guest â†’ localStorage cart
    const existing = updatedCart.find(item => item.productId === product.id);
    if (existing) {
      existing.quantity += 1;
    } else {
      updatedCart.push({
        productId: product.id,
        productName: product.name,
        price: product.price,
        quantity: 1,
        imageUrl: product.image
      });
    }
    setCart(updatedCart);
    localStorage.setItem('cart', JSON.stringify(updatedCart));
    setError('Login to save your cart permanently.');
  }
};

