                                        throw new Error('Order API is not available.');
                                      }

                                      const res = await orderFn(payload);

                                      // If payment required and server returned a payment URL or client secret, attempt to redirect/handle
                                      if (paymentMethod === 'Online') {
                                        const paymentInitiator =
                                          ordersAPI.initiatePayment ||
                                          ordersAPI.pay ||
                                          ordersAPI.createPayment ||
                                          (res && res.data && res.data.paymentUrl ? (() => ({ data: { paymentUrl: res.data.paymentUrl } })) : null);

                                        if (typeof paymentInitiator === 'function') {
                                          // Try to initiate payment using provided order response / payload
                                          try {
                                            const payRes = await paymentInitiator(res.data || res);
                                            const paymentUrl = payRes?.data?.paymentUrl || payRes?.data?.url || payRes?.paymentUrl;
                                            if (paymentUrl) {
                                              window.location.href = paymentUrl;
                                              return;
                                            }
                                          } catch (payErr) {
                                            console.warn('Payment initiation failed, falling back to order placed state', payErr);
                                          }
                                        }
                                      }

                                      // success: clear cart locally and server-side if possible
                                      try {
                                        if (typeof cartAPI.clear === 'function') await cartAPI.clear();
                                        else if (typeof cartAPI.empty === 'function') await cartAPI.empty();
                                        else if (typeof cartAPI.removeAll === 'function') await cartAPI.removeAll();
                                      } catch (e) {
                                        // non-fatal
                                        console.warn('Failed to clear backend cart:', e);
                                      }

                                      setCart([]);
                                      sessionStorage.removeItem('cart');
                                      localStorage.removeItem('cart');

                                      // reload orders and navigate to orders page
                                      try { await loadOrders(); } catch (_) {}
                                      setCurrentPage('orders');

                                      // show confirmation (use response message / order id if present)
                                      const orderId = (res?.data && (res.data.id || res.data.orderId)) || res?.id;
                                      alert(orderId ? `Order placed successfully (Order #${orderId})` : 'Order placed successfully');
                                    } catch (err) {
                                      console.error('Place order failed', err);
                                      setError(err.response?.data?.message || err.message || 'Failed to place order');
                                    } finally {
                                      setLoading(false);
                                    }
                                  }}
                                  className="px-6 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 disabled:opacity-60"
                                  disabled={loading}
                                >
                                  {loading ? 'Placing Order...' : 'Place Order'}
