                  const res = await adminAPI.createProduct(payload);
                  // refresh products after create
                  await loadProducts();
                  setError('');
                  alert('Product created successfully');
                } catch (err) {
                  console.error('Create product failed', err);
                  setError(err.response?.data?.message || err.message || 'Create product failed');
                } finally {
                  setLoading(false);
                }
              };

              const editProduct = async (product) => {
                try {
                  setLoading(true);
                  const name = window.prompt('Product name:', product.name) || product.name;
                  const brand = window.prompt('Brand:', product.brand || '') || product.brand;
                  const category = window.prompt('Category:', product.category || '') || product.category;
                  const priceRaw = window.prompt('Price (number):', (product.price || 0).toString());
                  const price = Number(priceRaw || product.price || 0);
                  const image = window.prompt('Image URL:', product.image || '') || product.image;
                  const description = window.prompt('Short description:', product.description || '') || product.description;
                  const specs = window.prompt('Specs (comma separated):', product.specs || '') || product.specs;

                  const payload = { name, brand, category, price, image, description, specs };
                  await adminAPI.updateProduct(product.id, payload);
                  // optimistic update by reloading products
                  await loadProducts();
                  setError('');
                  alert('Product updated successfully');
                } catch (err) {
                  console.error('Update product failed', err);
                  setError(err.response?.data?.message || err.message || 'Update product failed');
                } finally {
                  setLoading(false);
                }
              };

              const deleteProduct = async (product) => {
                try {
                  const ok = window.confirm(`Delete product "${product.name}"? This cannot be undone.`);
                  if (!ok) return;
                  setLoading(true);
                  await adminAPI.deleteProduct(product.id);
                  // remove locally for immediate feedback
                  setProducts(prev => (prev || []).filter(p => p.id !== product.id));
                  // attempt to refresh list
                  try { await loadProducts(); } catch (_) {}
                  setError('');
                  alert('Product deleted');
                } catch (err) {
                  console.error('Delete product failed', err);
                  setError(err.response?.data?.message || err.message || 'Delete product failed');
                } finally {
                  setLoading(false);
                }
              };

              // render JSX
              return (
                <div>
                  <div className="flex items-center justify-between mb-6">

                    <button
                      onClick={createProduct}
                      className="px-4 py-2 bg-green-600 text-white rounded-lg hover:bg-green-700"
                    >
                      {loading ? 'Processing...' : 'Create Product'}
                    </button>
                  </div>
                  <div className="mb-4 flex justify-end">
                    <button
                      onClick={() => setCurrentPage('products')}
                      className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700"
                    >
                      View Store
                    </button>
                  </div>
                  {(products || []).length === 0 ? (
                    <p className="text-sm text-gray-500">No products available</p>
                  ) : (
                    <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-6">
                      {(products || []).map((p) => (
                        <div key={p.id} className="bg-white rounded-xl shadow-md overflow-hidden">
                          <img src={p.image} alt={p.name} className="w-full h-40 object-cover" />
                          <div className="p-4">
                            <div className="flex items-start justify-between gap-4">
                              <div>
                                <p className="text-sm text-gray-500">{p.brand || p.category}</p>
                                <h3 className="text-lg font-bold text-gray-900">{p.name}</h3>
                              </div>
                              <div className="text-right">
                                <p className="text-xl font-bold text-blue-600">â‚¹{p.price}</p>
                                <p className="text-sm text-gray-600">{p.rating ? `${p.rating} â˜…` : ''}</p>
                              </div>
                            </div>

                            <p className="text-sm text-gray-600 mt-3 line-clamp-3">{p.description}</p>

                            <div className="flex items-center justify-between mt-4">
                              <div className="flex gap-2">
                                <button
                                  onClick={() => editProduct(p)}
                                  className="px-3 py-1 bg-yellow-100 text-yellow-700 rounded"
                                >
                                  Edit
                                </button>
                                <button
                                  onClick={() => deleteProduct(p)}
                                  className="px-3 py-1 bg-red-100 text-red-700 rounded"
                                >
                                  Delete
                                </button>
                              </div>
                              <div className="text-sm text-gray-500">{p.specs || ''}</div>
                            </div>
                          </div>
                        </div>
                      ))}
                    </div>
                  )}
                </div>
              );
            })()}
          </div>
        )}
        {/* ORDERS TAB */}
        {activeTab === 'orders' && (
          <div>
            <h2 className="text-2xl font-bold mb-6">Recent Orders</h2>
            <div className="overflow-x-auto">
              <table className="w-full text-sm">
                <thead className="border-b">
                  <tr>
                    <th className="text-left py-3 font-semibold text-gray-700">Order ID</th>
                    <th className="text-left py-3 font-semibold text-gray-700">Date</th>
                    <th className="text-left py-3 font-semibold text-gray-700">Amount</th>
                    <th className="text-left py-3 font-semibold text-gray-700">Status</th>
                  </tr>
                </thead>
                <tbody>
                  {(adminOrders || []).length === 0 ? (
                    <tr>
                      <td colSpan="4" className="py-8 text-center text-gray-500">
                        No orders yet
                      </td>
                    </tr>
                  ) : (
                    (adminOrders || []).map((order) => (
                      <tr key={order.id} className="border-b hover:bg-gray-50">
                        <td className="py-3 font-medium">#{order.id}</td>
                        <td className="py-3 text-gray-600">{order.createdAt ? new Date(order.createdAt).toLocaleDateString() : ''}</td>
                        <td className="py-3 font-semibold">â‚¹{(order.totalAmount || 0).toLocaleString()}</td>
                        <td className="py-3">
                          <span className={`px-3 py-1 rounded-full text-xs font-semibold ${order.status === 'Pending' ? 'bg-yellow-100 text-yellow-700' : 'bg-green-100 text-green-700'}`}>
                            {order.status || 'N/A'}
                          </span>
                        </td>
                      </tr>
                    ))
                  )}
                </tbody>
              </table>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}
}                                                                                             
export default CompleteIntegratedApp;
