            <script type="text/javascript" dangerouslySetInnerHTML={{__html: ""}} />

            {(() => {
              // define functions inside IIFE so they are in component scope
              const createProduct = async () => {
                try {
                  setLoading(true);
                  const name = window.prompt('Product name:');
                  if (!name) return;
                  const brand = window.prompt('Brand:', 'BrandName') || '';
                  const category = window.prompt('Category:', 'General') || '';
                  const priceRaw = window.prompt('Price (number):', '0');
                  const price = Number(priceRaw || 0);
                  const image = window.prompt('Image URL:', 'https://via.placeholder.com/300') || '';
                  const description = window.prompt('Short description:', '') || '';
                  const specs = window.prompt('Specs (comma separated):', '') || '';

                  const payload = { name, brand, category, price, image, description, specs };
                  const res = await adminAPI.createProduct(payload);
                  // refresh products after create
                  await loadProducts();
                  setError('');
                  alert('Product created successfully');
