                  console.error('Create product failed', err);
                  setError(err.response?.data?.message || err.message || 'Create product failed');
                } finally {
                  setLoading(false);
                }
              };

              const editProduct = async (product) => {
                try {
                  setLoading(true);
                  const name = window.prompt('Product name:', product.name) || product.name;
                  const brand = window.prompt('Brand:', product.brand || '') || product.brand;
                  const category = window.prompt('Category:', product.category || '') || product.category;
                  const priceRaw = window.prompt('Price (number):', (product.price || 0).toString());
                  const price = Number(priceRaw || product.price || 0);
                  const image = window.prompt('Image URL:', product.image || '') || product.image;
                  const description = window.prompt('Short description:', product.description || '') || product.description;
                  const specs = window.prompt('Specs (comma separated):', product.specs || '') || product.specs;

                  const payload = { name, brand, category, price, image, description, specs };
                  await adminAPI.updateProduct(product.id, payload);
                  // optimistic update by reloading products
                  await loadProducts();
                  setError('');
                  alert('Product updated successfully');
                } catch (err) {
                  console.error('Update product failed', err);
                  setError(err.response?.data?.message || err.message || 'Update product failed');
                } finally {
                  setLoading(false);
                }
              };

              const deleteProduct = async (product) => {
                try {
                  const ok = window.confirm(`Delete product "${product.name}"? This cannot be undone.`);
                  if (!ok) return;
                  setLoading(true);
                  await adminAPI.deleteProduct(product.id);
                  // remove locally for immediate feedback
                  setProducts(prev => (prev || []).filter(p => p.id !== product.id));
                  // attempt to refresh list
                  try { await loadProducts(); } catch (_) {}
                  setError('');
                  alert('Product deleted');
                } catch (err) {
                  console.error('Delete product failed', err);
                  setError(err.response?.data?.message || err.message || 'Delete product failed');
                } finally {
                  setLoading(false);
                }
              };

