import React, { useState, useEffect, useRef } from 'react';
import { Mail, Lock, User, Phone, Eye, EyeOff, ShoppingBag, TrendingUp, Package, Users, BarChart3, Settings, MessageCircle, Search, X, Send, ShoppingCart, LogOut, UserCircle, MapPin, CreditCard, Clock, Plus, Minus, Trash2 } from 'lucide-react';
import {
  authAPI,
  productsAPI,
  cartAPI,
  ordersAPI,
  recommendationsAPI
} from './api'; // adjust path if needed


import axios from 'axios';

/*// API Configuration
const API_BASE_URL = 'http://localhost:5033/api';

const api = axios.create({
  baseURL: API_BASE_URL,
  headers: {
    'Content-Type': 'application/json',
  },
});

// Request interceptor to add auth token
api.interceptors.request.use(
  (config) => {
    const token = sessionStorage.getItem('token');
    if (token) {
      config.headers.Authorization = `Bearer ${token}`;
    }
    return config;
  },
  (error) => Promise.reject(error)
);

// API endpoints
const authAPI = {
  login: (email, password) => 
    api.post('/auth/login', { email, password }),
  register: (fullName, email, mobile, password) =>
    api.post('/auth/register', { fullName, email, mobile, password }),
  getProfile: () => 
    api.get('/user/profile'),
};

const productsAPI = {
  getAll: () => 
    api.get('/products'),
  search: (query, categoryId, minPrice, maxPrice) =>
    api.get('/products/search', { params: { query, categoryId, minPrice, maxPrice } }),
  getById: (id) => 
    api.get(`/products/${id}`),
};

const cartAPI = {
  view: () => 
    api.get('/cart/view'),
  add: (productId, quantity = 1) =>
    api.post('/cart/add', { productId, quantity }),
  remove: (productId) =>
    api.delete(`/cart/remove/${productId}`),
  update: (productId, quantity) =>
    api.put('/cart/update', { productId, quantity }),
};

const ordersAPI = {
  confirm: (shippingAddressId) =>
    api.post('/order/confirm', { shippingAddressId }),
  history: () =>
    api.get('/order/history'),
  getById: (id) =>
    api.get(`/order/${id}`),
};

const recommendationsAPI = {
  rank: (productIds, criteria) =>
    api.post('/recommendations/rank', { productIds, criteria }),
};*/

const CompleteIntegratedApp = () => {
  const [currentPage, setCurrentPage] = useState('home');
  const [user, setUser] = useState(null);
  const [showPassword, setShowPassword] = useState(false);
  const [loginData, setLoginData] = useState({ email: '', password: '' });
  const [registerData, setRegisterData] = useState({ fullName: '', email: '', mobile: '', password: '', confirmPassword: '' });
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const [activeTab, setActiveTab] = useState('overview');

  // Shopping state
  const [products, setProducts] = useState([]);
  const [cart, setCart] = useState([]);
  const [orders, setOrders] = useState([]);
  const [chatOpen, setChatOpen] = useState(false);
  const [chatMessages, setChatMessages] = useState([]);
  const [currentMessage, setCurrentMessage] = useState('');
  const [isTyping, setIsTyping] = useState(false);
  const chatEndRef = useRef(null);
  const [editMode, setEditMode] = useState(false);
  const [loadingProfile, setLoadingProfile] = useState(false);


  //profile state 2
  const [profileData, setProfileData] = useState({
  fullName: '',
  email: '',
  mobile: '',
  addresses: []
});

// In your component where you have fetchProfile

const fetchProfile = async () => {
    try {
        setLoadingProfile(true);
        const res = await authAPI.getProfile();
        console.log('Profile API response:', res.data);

        // Backend returns UserProfileDto directly
        const data = res.data;

        setProfileData({
            fullName: data.fullName || user?.name || '',
            email: data.email || user?.email || '',
            mobile: data.mobile || user?.mobile || '+91 00000 00000', // Default fallback
            addresses: data.addresses || []
        });
    } catch (err) {
        console.error('Profile fetch error:', err);
        // Fallback to user state if API fails
        if (user) {
            setProfileData({
                fullName: user.name || '',
                email: user.email || '',
                mobile: user.mobile || '+91 00000 00000',
                addresses: []
            });
        }
    } finally {
        setLoadingProfile(false);
    }
};

const fetchOrders = async () => {
  try {
    const res = await ordersAPI.history();
    setOrders(res.data || []);
  } catch (err) {
    console.error('Order fetch error:', err);
  }
};

useEffect(() => {
  if (user && currentPage === 'profile') {
    fetchProfile();
    fetchOrders();
  }
}, [user, currentPage, fetchProfile, fetchOrders]);


const loadOrders = async () => {
  try {
    const res = await ordersAPI.history();
    setOrders(res.data || []);
  } catch (err) {
    console.error('Orders fetch failed', err);
  }
};

const handleSaveProfile = async () => {
  setEditMode(false);
  // Add API call to save profile if needed
};

/*const [orders, setOrders] = useState([]);
const [editMode, setEditMode] = useState(false);
const [loadingProfile, setLoadingProfile] = useState(false);

  // Profile state
  const [editMode, setEditMode] = useState(false);
  const [profileData, setProfileData] = useState({
    fullName: '',
    email: '',
    mobile: '',
    addresses: []
  });*/

  // Check for existing session on mount
  useEffect(() => {
    const token = sessionStorage.getItem('token');
    const userData = sessionStorage.getItem('user');
    if (token && userData) {
      try {
        setUser(JSON.parse(userData));
        setCurrentPage('products');
        loadUserData();
      } catch (err) {
        console.error('Error parsing user data:', err);
        sessionStorage.removeItem('token');
        sessionStorage.removeItem('user');
      }
    }
  }, []);

  // Load user data (profile, cart, products)
  const loadUserData = async () => {
    try {
      await Promise.all([
        loadProducts(),
        loadCart(),
        loadProfile(),
        loadOrders()
      ]);
    } catch (err) {
      console.error('Error loading user data:', err);
    }
  };

 // Load products from backend
  const loadProducts = async () => {
    try {
      const response = await productsAPI.getAll();
      setProducts(response.data);
    } catch (err) {
      console.error('Error loading products:', err);
      // Fallback to sample data if backend fails
      setProducts(getSampleProducts());
    }
  };

  // Load cart from backend
  const loadCart = async () => {
    if (!user) return;
    try {
      const response = await cartAPI.view();
      setCart(response.data.items || []);
    } catch (err) {
      console.error('Error loading cart:', err);
    }
  };

  // Load profile from backend
  const loadProfile = async () => {
    if (!user) return;
    try {
      const response = await authAPI.getProfile();
      setProfileData({
        fullName: response.data.fullName || '',
        email: response.data.email || '',
        mobile: (response.data.mobile || response.data.Mobile || '').toString().trim(),
        addresses: response.data.addresses || []
      });
    } catch (err) {
      console.error('Error loading profile:', err);
    }
  };

 /* // Load order history
  const loadOrders = async () => {
    if (!user) return;
    try {
      const response = await ordersAPI.history();
      setOrders(response.data || []);
    } catch (err) {
      console.error('Error loading orders:', err);
    }
  };*/

  // Sample products fallback
  const getSampleProducts = () => [
    {
      id: 1,
      name: 'Premium Smartphone X1',
      brand: 'TechBrand',
      price: 9999,
      rating: 4.5,
      reviewCount: 1250,
      image: 'https://via.placeholder.com/300x300/3b82f6/ffffff?text=Smartphone',
      category: 'Electronics',
      description: 'Powerful processor, stunning camera, all-day battery',
      specs: '6GB RAM, 128GB Storage, 48MP Camera'
    },
    {
      id: 2,
      name: 'Wireless Earbuds Pro',
      brand: 'AudioMax',
      price: 2999,
      rating: 4.7,
      reviewCount: 890,
      image: 'https://via.placeholder.com/300x300/8b5cf6/ffffff?text=Earbuds',
      category: 'Audio',
      description: 'Active noise cancellation, 30hr battery life',
      specs: 'Bluetooth 5.2, IPX4 Water Resistant'
    },
    {
      id: 3,
      name: 'Smart Watch Ultra',
      brand: 'FitTech',
      price: 4999,
      rating: 4.3,
      reviewCount: 567,
      image: 'https://via.placeholder.com/300x300/10b981/ffffff?text=Watch',
      category: 'Wearables',
      description: 'Health tracking, GPS, always-on display',
      specs: 'Heart Rate Monitor, Sleep Tracking, 7-day Battery'
    },
    {
      id: 4,
      name: 'Gaming Laptop Z9',
      brand: 'GameForce',
      price: 59999,
      rating: 4.8,
      reviewCount: 432,
      image: 'https://via.placeholder.com/300x300/ef4444/ffffff?text=Laptop',
      category: 'Computers',
      description: 'High-performance gaming, RGB keyboard',
      specs: 'RTX 3060, 16GB RAM, 512GB SSD, 144Hz Display'
    },
    {
      id: 5,
      name: 'Camera DSLR Pro',
      brand: 'PhotoMax',
      price: 45999,
      rating: 4.6,
      reviewCount: 289,
      image: 'https://via.placeholder.com/300x300/f59e0b/ffffff?text=Camera',
      category: 'Photography',
      description: 'Professional photography, 4K video',
      specs: '24MP Sensor, Dual Card Slots, Weather Sealed'
    },
    {
      id: 6,
      name: 'Tablet Pro 11',
      brand: 'TechBrand',
      price: 34999,
      rating: 4.4,
      reviewCount: 678,
      image: 'https://via.placeholder.com/300x300/06b6d4/ffffff?text=Tablet',
      category: 'Electronics',
      description: 'Stunning display, all-day battery, perfect for work',
      specs: '11-inch Display, 128GB Storage, Stylus Support'
    }
  ];

const handleLogin = async () => {
  setError('');
  setLoading(true);

  try {
    const response = await authAPI.login(
      loginData.email.trim(),
      loginData.password
    );

    const data = response.data;

    if (!data?.token || !data?.role) {
      throw new Error('Invalid login response');
    }

    // Persist session
    localStorage.setItem('token', data.token);
    localStorage.setItem('user', JSON.stringify(data));

    // Global user state
    setUser({
      email: data.email,
      role: data.role,
      name: data.fullName,
      mobile: data.mobile || ''
    });

    // Profile only for customers
    if (data.role === 'Customer') {
      setProfileData({
        fullName: data.fullName || '',
        email: data.email || '',
        mobile: data.mobile || '',
        addresses: []
      });
    }

    // ðŸ” ROLE-BASED REDIRECT
    if (data.role === 'Admin') {
      setActiveTab('overview');      // connect dashboard
      setCurrentPage('admin');
    } else {
      setCurrentPage('products');
    }

  } catch (err) {
    setError(
      err.response?.data?.message ||
      err.message ||
      'Invalid credentials'
    );
  } finally {
    setLoading(false);
  }
};

const handleRegister = async () => {
  const { fullName, email, mobile, password, confirmPassword } = registerData;

  // Validation
  if (!fullName?.trim()) {
    setError('Full name is required');
    return;
  }

  if (!email?.trim()) {
    setError('Email is required');
    return;
  }

  if (!mobile?.trim()) {
    setError('Mobile number is required');
    return;
  }

  if (!password) {
    setError('Password is required');
    return;
  }

  if (password !== confirmPassword) {
    setError('Passwords do not match');
    return;
  }

  if (password.length < 6) {
    setError('Password must be at least 6 characters');
    return;
  }

  setLoading(true);
  setError('');

  try {
    console.log('Attempting registration:', { fullName, email, mobile });

    const response = await authAPI.register(
      fullName.trim(),
      email.trim(),
      mobile.trim(),
      password
    );

    // backend must return role = "Customer"
    if (response.data.role !== 'Customer') {
      throw new Error('Invalid user role');
    }


    console.log('Registration response:', response.data);

    const data = response.data;

    // Validate response
    if (!data.token) {
      throw new Error('No token received from server');
    }

    // Store token and user data
    localStorage.setItem('token', data.token);
    localStorage.setItem('user', JSON.stringify(data));

    // Set user state
    setUser({
      email: data.email,
      role: data.role,
      name: data.fullName,
      mobile: data.mobile || mobile
    });

    // Set profile data
    setProfileData({
      fullName: data.fullName || fullName,
      email: data.email || email,
      mobile: data.mobile || mobile,
      addresses: []
    });

    console.log('Registration successful');

    // Redirect to products (new users are always customers)
    setCurrentPage('products');

  } catch (err) {
    console.error('Registration error:', err);
    console.error('Error response:', err.response?.data);

    const errorMessage = err.response?.data?.message || 
                        err.response?.data?.title ||
                        err.response?.data?.errors?.Email?.[0] ||
                        err.message || 
                        'Registration failed. Please try again.';

    setError(errorMessage);
  } finally {
    setLoading(false);
  }
};                           

const handleLogout = () => {
  // Clear storage
  localStorage.removeItem('token');
  localStorage.removeItem('user');

  // Reset all state
  setUser(null);
  setCurrentPage('home');
  setCart([]);
  setOrders([]);
  setChatMessages([]);
  setChatOpen(false);
  setProfileData({
    fullName: '',
    email: '',
    mobile: '',
    addresses: []
  });

  console.log('User logged out successfully');
};
// At the top of your component, after state declarations
useEffect(() => {
  const token = localStorage.getItem('token');
  const savedUser = localStorage.getItem('user');

  if (!token || !savedUser) return;

  try {
    const userData = JSON.parse(savedUser);

    if (!userData.role || !userData.email) {
      throw new Error('Invalid session');
    }

    setUser({
      email: userData.email,
      role: userData.role,
      name: userData.fullName || userData.name,
      mobile: userData.mobile || ''
    });

    if (userData.role === 'Admin') {
      setActiveTab('overview');
      setCurrentPage('admin');
    } else {
      setCurrentPage('products');
    }

  } catch {
    localStorage.clear();
    setUser(null);
    setCurrentPage('home');
  }
}, []);


  const addToCart = async (product) => {
    try {
      await cartAPI.add(product.id, 1);
      await loadCart();
    } catch (err) {
      console.error('Error adding to cart:', err);
      setError('Failed to add item to cart');
    }
  };

  const updateCartQuantity = async (productId, quantity) => {
    if (quantity <= 0) {
      await removeFromCart(productId);
      return;
    }

    try {
      await cartAPI.update(productId, quantity);
      await loadCart();
    } catch (err) {
      console.error('Error updating cart:', err);
      setError('Failed to update cart');
    }
  };

  const removeFromCart = async (productId) => {
    try {
      await cartAPI.remove(productId);
      await loadCart();
    } catch (err) {
      console.error('Error removing from cart:', err);
      setError('Failed to remove item from cart');
    }
  };

  // AI Chat simulation
  const sendMessageToAgent = async (message) => {
    setIsTyping(true);

    const userMsg = { role: 'user', content: message };
    setChatMessages(prev => [...prev, userMsg]);
    setCurrentMessage('');

    setTimeout(() => {
      let agentResponse = '';
      const lowerMsg = message.toLowerCase();

      if (lowerMsg.includes('hi') || lowerMsg.includes('hello')) {
        agentResponse = "Hello! I'm your AI shopping assistant. I can help you find products, add items to cart, or answer questions about your orders. What are you looking for today?";
      } else if (lowerMsg.includes('smartphone') || lowerMsg.includes('phone')) {
        const phones = products.filter(p => p.category === 'Electronics');
        if (lowerMsg.includes('under') || lowerMsg.includes('below')) {
          agentResponse = "I found some great smartphones under â‚¹10,000! Here's the best option:\n\nðŸ“± Premium Smartphone X1 by TechBrand\nPrice: â‚¹9,999\nRating: 4.5/5 (1,250 reviews)\nFeatures: 6GB RAM, 128GB Storage, 48MP Camera\n\nWould you like me to add this to your cart?";
        } else {
