import axios from 'axios';

const API_BASE_URL = 'http://localhost:5033/api';

// Create axios instance
const api = axios.create({
  baseURL: API_BASE_URL,
  headers: {
    'Content-Type': 'application/json',
    'Accept': 'application/json'
  }
});

  // Address API (add / save / list / delete)
  // Backend should associate addresses with the authenticated user (via JWT);
  // remove client-side table injection and provide localStorage fallback so addresses persist across logout.
  const LOCAL_ADDR_KEY = 'local_addresses';

  function getLocalAddresses() {
  // migrate legacy key and ensure local storage is initialized
  try {
    const legacy = localStorage.getItem('addresses');
    if (legacy) {
      const parsed = JSON.parse(legacy);
      if (Array.isArray(parsed) && parsed.length) {
        setLocalAddresses(parsed);
      }
      localStorage.removeItem('addresses');
    }
  } catch (e) {}
  try {
    const raw = localStorage.getItem(LOCAL_ADDR_KEY);
    if (!raw) {
      setLocalAddresses([]);
      return [];
    }
    const parsed = JSON.parse(raw);
    if (Array.isArray(parsed)) return parsed;
    // reset if stored value is invalid
    setLocalAddresses([]);
    return [];
  } catch (e) {
    // on error return empty array
    return [];
  }
  }
  export async function syncLocalAddresses() {
    const local = getLocalAddresses();
    if (!local || !local.length) return;
    try {
      const res = await api.get('/user/addresses');
      const server = Array.isArray(res.data) ? res.data : [];
      const serverIds = new Set(server.map(a => a.id));
      for (const addr of local) {
        if (!serverIds.has(addr.id)) {
          const payload = { ...addr };
          delete payload.id;
          try {
            const r = await api.post('/user/addresses', payload);
            if (r && r.data) {
              removeLocalAddress(addr.id);
              upsertLocalAddress(r.data);
            }
          } catch (e) {
            // ignore individual address upload failures
          }
        }
      }
    } catch (e) {
      // unable to fetch server addresses; keep local data
    }
  }

  export function clearLocalAddresses() {
    setLocalAddresses([]);
  }

  export function getLocalAddressById(id) {
    return getLocalAddresses().find(a => a.id === id) || null;
  }
  function setLocalAddresses(addrs) {
    try {
      localStorage.setItem(LOCAL_ADDR_KEY, JSON.stringify(addrs));
    } catch (e) {}
  }

  function upsertLocalAddress(addr) {
    const list = getLocalAddresses();
    const idx = list.findIndex(a => a.id === addr.id);
    if (idx >= 0) list[idx] = addr;
    else list.push(addr);
    setLocalAddresses(list);
  }

  function removeLocalAddress(id) {
    setLocalAddresses(getLocalAddresses().filter(a => a.id !== id));
  }

  export const addressAPI = {
    getAll: () =>
      api.get('/user/addresses')
        .catch(() => Promise.resolve({ data: getLocalAddresses() })),

    add: (address) =>
      api.post('/user/addresses', address)
        .then(res => {
          if (res && res.data) upsertLocalAddress(res.data);
          return res;
        })
        .catch(() => {
          // fallback: save locally (temporary id)
          const local = { ...address, id: Date.now() };
          upsertLocalAddress(local);
          return Promise.resolve({ data: local, __local: true });
        }),

    save: (id, address) =>
      api.put(`/user/addresses/${id}`, address)
        .then(res => {
          if (res && res.data) upsertLocalAddress(res.data);
          return res;
        })
        .catch(() => {
          const local = { ...address, id };
          upsertLocalAddress(local);
          return Promise.resolve({ data: local, __local: true });
        }),

    remove: (id) =>
      api.delete(`/user/addresses/${id}`)
        .then(res => {
          removeLocalAddress(id);
          return res;
        })
        .catch(() => {
          removeLocalAddress(id);
          return Promise.resolve({ data: { id }, __local: true });
        }),
  };


// ✅ Always attach JWT token (check sessionStorage OR localStorage)
api.interceptors.request.use(
  (config) => {
    const token = sessionStorage.getItem('token') || localStorage.getItem('token');
    if (token) {
      config.headers = config.headers || {};
      config.headers.Authorization = `Bearer ${token}`;
    }
    return config;
  },
  (error) => Promise.reject(error)
);

/* ===================== AUTH API ===================== */

export const authAPI = {
  login: (email, password) =>
    api.post('/auth/login', { email, password }),

  register: (fullName, email, mobile, password) =>
    api.post('/auth/register', { fullName, email, mobile, password }),

  getProfile: () =>
    api.get('/user/profile'),
};

/* ===================== PRODUCTS API ===================== */

export const productsAPI = {
  getAll: () =>
    api.get('/products'),

  search: (query, categoryId, minPrice, maxPrice) =>
    api.get('/products/search', {
      params: { query, categoryId, minPrice, maxPrice }
    }),

  getById: (id) =>
    api.get(`/products/${id}`),
};

/* ===================== CART API (FIXED) ===================== */

export const cartAPI = {
  // View cart
  view: () =>
    api.get('/cart/view'),

  // ✅ FIXED: accept DTO object
  add: (data) =>
    api.post('/cart/add', data),

  // Remove item
  remove: (productId) =>
    api.delete(`/cart/remove/${productId}`),

  // Update quantity
  update: (productId, quantity) =>
    api.put('/cart/update', { productId, quantity }),

  // Clear cart (optional but useful)
  clear: () =>
    api.delete('/cart/clear'),
};

/* ===================== ORDERS API ===================== */

export const ordersAPI = {
  confirm: (shippingAddressId) =>
    api.post('/order/confirm', { shippingAddressId }),

  history: () =>
    api.get('/order/history'),

  getById: (id) =>
    api.get(`/order/${id}`),
};

/* ===================== AI RECOMMENDATIONS API ===================== */

export const recommendationsAPI = {
  rank: (productIds, criteria) =>
    api.post('/recommendations/rank', { productIds, criteria }),
};

/* ===================== PAYMENT API ===================== */

export const paymentAPI = {
  initiate: (orderId, amount, paymentMethod) =>
    api.post('/payment/initiate', { orderId, amount, paymentMethod }),

  verify: (paymentId, transactionId) =>
    api.post('/payment/verify', { paymentId, transactionId }),

  getStatus: (paymentId) =>
    api.get(`/payment/status/${paymentId}`),
};

/* ===================== INVENTORY API ===================== */

export const inventoryAPI = {
  getStock: (productId) =>
    api.get(`/inventory/stock/${productId}`),

  checkAvailability: (productId, quantity) =>
    api.get('/inventory/check', {
      params: { productId, quantity }
    }),
};

/* ===================== ADMIN API ===================== */
export function fetchUserDetails(userId) {
  return api.get(`/admin/users/${userId}`);
}
export const adminAPI = {
  getDashboard: () =>
    api.get('/admin/dashboard'),

  getUsers: () =>
    api.get('/admin/users'),
    placeOrder: (orderData) =>
      api.post('/order/place', orderData),

  getOrders: () =>
    api.get('/admin/orders'),

  createProduct: (data) =>
    api.post('/admin/products', data),

  updateProduct: (id, data) =>
    api.put(`/admin/products/${id}`, data),

  deleteProduct: (id) =>
    api.delete(`/admin/products/${id}`),

  fetchUserDetails: (id) => api.get(`/admin/users/${id}`).then(r => r.data)
};
// Remove duplicate export statement
export default api;
